import React, { useState } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  Grid,
} from '@mui/material';
import {
  Gavel,
  Description,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';

interface RequestType {
  id: string;
  label: string;
  description: string;
  icon: React.ReactNode;
}

const AddRequest1: React.FC = () => {
  const navigate = useNavigate();
  const [selectedOption, setSelectedOption] = useState<string | null>(null);

  const options: RequestType[] = [
    {
      id: 'judicial',
      label: 'Judicial',
      description: 'e.g. Certified Copies, Court Case Follow-ups, Surety/Bail Bonds etc.',
      icon: <Gavel />,
    },
    {
      id: 'non_judicial',
      label: 'Non-Judicial',
      description: 'e.g. Birth Certificate, Death Certificate, Power of Attorney etc.',
      icon: <Description />,
    },
  ];

  const handleNext = () => {
    if (selectedOption) {
      navigate('/add-request-2', { state: { type: selectedOption } });
    }
  };

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom fontWeight="bold">
        Add Request
      </Typography>

      <Card>
        <CardContent>
          <Box sx={{ mb: 4 }}>
            <Typography variant="h6" gutterBottom>
              Select Type
            </Typography>
          </Box>

          <Grid container spacing={3}>
            {options.map((option) => (
              <Grid item xs={12} sm={6} key={option.id}>
                <Card
                  sx={{
                    cursor: 'pointer',
                    border: selectedOption === option.id ? `2px solid #FFA500` : '1px solid #D1D3D4',
                    borderRadius: 3,
                    backgroundColor: selectedOption === option.id ? '#FFA500' : 'white',
                    color: selectedOption === option.id ? 'white' : 'inherit',
                    '&:hover': { borderColor: '#FFA500' },
                    height: 200,
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                  }}
                  onClick={() => setSelectedOption(option.id)}
                >
                  <CardContent sx={{ textAlign: 'center' }}>
                    <Box sx={{ 
                      color: selectedOption === option.id ? 'white' : Colors.primary, 
                      mb: 2,
                      display: 'flex',
                      justifyContent: 'center'
                    }}>
                      {option.icon}
                    </Box>
                    <Typography variant="h6" fontWeight="bold" gutterBottom>
                      {option.label}
                    </Typography>
                    <Typography variant="body2" color={selectedOption === option.id ? 'white' : 'textSecondary'}>
                      {option.description}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>

          <Button
            variant="contained"
            fullWidth
            disabled={!selectedOption}
            onClick={handleNext}
            sx={{
              backgroundColor: selectedOption ? '#FFA500' : '#D3D3D3',
              mt: 4,
              borderRadius: 2,
              height: 50,
            }}
          >
            Next
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
};

export default AddRequest1;
