  Paper,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stepper,
  Step,
  StepLabel,
  Alert,
  RadioGroup,
  FormControlLabel,
  Radio,
} from '@mui/material';
import {
  TrendingUp,
  Notifications,
  Add,
  Assignment,
  CalendarToday,
  Chat,
  Description,
  PersonAdd,
  People,
  Gavel,
} from '@mui/icons-material';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';
import Breadcrumbs from '../components/Breadcrumbs';

interface DashboardStats {
  totalCases: number;
  activeCases: number;
  totalClients: number;
  totalRequests: number;
  totalMatters: number;
  recentActivity: number;
}

interface RecentCase {
  _id: string;
  title: string;
  client: string;
  status: string;
  date: string;
  category: string;
}

interface CaseCategory {
  _id: string;
  name: string;
  icon: string;
}

interface CaseSubcategory {
  _id: string;
  name: string;
  icon: string;
  parent: string;
}

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState<DashboardStats>({
    totalCases: 0,
    activeCases: 0,
    totalClients: 0,
    totalRequests: 0,
    totalMatters: 0,
    recentActivity: 0,
  });
  const [recentCases, setRecentCases] = useState<RecentCase[]>([]);
  const [openCaseDialog, setOpenCaseDialog] = useState(false);
  const [activeStep, setActiveStep] = useState(0);
  const [categories, setCategories] = useState<CaseCategory[]>([]);
  const [selectedCategory, setSelectedCategory] = useState<CaseCategory | null>(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState<CaseSubcategory | null>(null);
  const [newCase, setNewCase] = useState({ title: '', client: '', case_number: '', party_1: '', case_description: '' });
  const [showDraftSaved, setShowDraftSaved] = useState(false);

  // Mock data matching Android app structure exactly
  const mockCategories: CaseCategory[] = [
    { _id: '1', name: 'Family Law', icon: 'family' },
    { _id: '2', name: 'Civil Law', icon: 'civil' },
    { _id: '3', name: 'Employment Law', icon: 'employment' },
    { _id: '4', name: 'Criminal Law', icon: 'criminal' },
    { _id: '5', name: 'Corporate Law', icon: 'corporate' },
    { _id: '6', name: 'Banking Law', icon: 'banking' },
    { _id: '7', name: 'Taxation Law', icon: 'taxation' },
    { _id: '8', name: 'Property Law', icon: 'property' },
  ];

  const mockSubcategories: { [key: string]: CaseSubcategory[] } = {
    '1': [ // Family Law
      { _id: '1-1', name: 'Divorce', icon: 'divorce', parent: '1' },
      { _id: '1-2', name: 'Child Custody', icon: 'custody', parent: '1' },
      { _id: '1-3', name: 'Child Support', icon: 'support', parent: '1' },
      { _id: '1-4', name: 'Adoption', icon: 'adoption', parent: '1' },
      { _id: '1-5', name: 'Marriage Registration', icon: 'marriage', parent: '1' },
      { _id: '1-6', name: 'Domestic Violence', icon: 'violence', parent: '1' },
    ],
    '2': [ // Civil Law
      { _id: '2-1', name: 'Contract Disputes', icon: 'contract', parent: '2' },
      { _id: '2-2', name: 'Property Disputes', icon: 'property', parent: '2' },
      { _id: '2-3', name: 'Tort Claims', icon: 'tort', parent: '2' },
      { _id: '2-4', name: 'Landlord-Tenant', icon: 'rental', parent: '2' },
      { _id: '2-5', name: 'Personal Injury', icon: 'injury', parent: '2' },
      { _id: '2-6', name: 'Medical Malpractice', icon: 'malpractice', parent: '2' },
    ],
    '3': [ // Employment Law
      { _id: '3-1', name: 'Employment Contracts', icon: 'contract', parent: '3' },
      { _id: '3-2', name: 'Workplace Safety', icon: 'safety', parent: '3' },
      { _id: '3-3', name: 'Industrial Disputes', icon: 'disputes', parent: '3' },
      { _id: '3-4', name: 'Workers Compensation', icon: 'compensation', parent: '3' },
      { _id: '3-5', name: 'Trade Unions', icon: 'unions', parent: '3' },
      { _id: '3-6', name: 'Minimum Wage', icon: 'wage', parent: '3' },
    ],
    '4': [ // Criminal Law
      { _id: '4-1', name: 'Murder', icon: 'murder', parent: '4' },
      { _id: '4-2', name: 'Theft', icon: 'theft', parent: '4' },
      { _id: '4-3', name: 'Assault', icon: 'assault', parent: '4' },
      { _id: '4-4', name: 'Drug Offenses', icon: 'drugs', parent: '4' },
      { _id: '4-5', name: 'Fraud', icon: 'fraud', parent: '4' },
      { _id: '4-6', name: 'Cyber Crime', icon: 'cyber', parent: '4' },
    ],
    '5': [ // Corporate Law
      { _id: '5-1', name: 'Company Registration', icon: 'registration', parent: '5' },
      { _id: '5-2', name: 'Corporate Governance', icon: 'governance', parent: '5' },
      { _id: '5-3', name: 'Mergers & Acquisitions', icon: 'mergers', parent: '5' },
      { _id: '5-4', name: 'Intellectual Property', icon: 'ip', parent: '5' },
      { _id: '5-5', name: 'Employment Law', icon: 'employment', parent: '5' },
      { _id: '5-6', name: 'Tax Law', icon: 'tax', parent: '5' },
    ],
    '6': [ // Banking Law
      { _id: '6-1', name: 'Banking Regulations', icon: 'regulations', parent: '6' },
      { _id: '6-2', name: 'Securities Law', icon: 'securities', parent: '6' },
      { _id: '6-3', name: 'Insurance Law', icon: 'insurance', parent: '6' },
      { _id: '6-4', name: 'Debt Recovery', icon: 'debt', parent: '6' },
      { _id: '6-5', name: 'Financial Fraud', icon: 'fraud', parent: '6' },
      { _id: '6-6', name: 'Investment Law', icon: 'investment', parent: '6' },
    ],
    '7': [ // Taxation Law
      { _id: '7-1', name: 'Income Tax', icon: 'income', parent: '7' },
      { _id: '7-2', name: 'Corporate Tax', icon: 'corporate', parent: '7' },
      { _id: '7-3', name: 'Sales Tax', icon: 'sales', parent: '7' },
      { _id: '7-4', name: 'Property Tax', icon: 'property', parent: '7' },
      { _id: '7-5', name: 'Tax Disputes', icon: 'disputes', parent: '7' },
      { _id: '7-6', name: 'Tax Planning', icon: 'planning', parent: '7' },
    ],
    '8': [ // Property Law
      { _id: '8-1', name: 'Real Estate', icon: 'realestate', parent: '8' },
      { _id: '8-2', name: 'Property Registration', icon: 'registration', parent: '8' },
      { _id: '8-3', name: 'Land Disputes', icon: 'land', parent: '8' },
      { _id: '8-4', name: 'Property Tax', icon: 'tax', parent: '8' },
      { _id: '8-5', name: 'Rental Agreements', icon: 'rental', parent: '8' },
      { _id: '8-6', name: 'Property Development', icon: 'development', parent: '8' },
    ],
  };

  // Auto-save draft functionality
  const saveDraft = () => {
    const draft = {
      title: newCase.title,
      client: newCase.client,
      case_number: newCase.case_number,
      party_1: newCase.party_1,
      case_description: newCase.case_description,
      selectedCategory: selectedCategory?._id,
      selectedSubcategory: selectedSubcategory?._id,
      activeStep,
      timestamp: new Date().toISOString(),
    };
    localStorage.setItem('case-draft', JSON.stringify(draft));
    setShowDraftSaved(true);
    setTimeout(() => setShowDraftSaved(false), 2000);
  };

  // Load draft on component mount
  useEffect(() => {
    const savedDraft = localStorage.getItem('case-draft');
    if (savedDraft) {
      try {
        const draft = JSON.parse(savedDraft);
        setNewCase({
          title: draft.title || '',
          client: draft.client || '',
          case_number: draft.case_number || '',
          party_1: draft.party_1 || '',
          case_description: draft.case_description || '',
        });
        setActiveStep(draft.activeStep || 0);

        // Restore category and subcategory selections
        if (draft.selectedCategory) {
          const category = mockCategories.find(c => c._id === draft.selectedCategory);
          if (category) {
            setSelectedCategory(category);
          }
        }

        if (draft.selectedSubcategory) {
          const subcategory = mockSubcategories[selectedCategory?._id || '']?.find(s => s._id === draft.selectedSubcategory);
          if (subcategory) {
            setSelectedSubcategory(subcategory);
          }
        }
      } catch (error) {
        console.error('Error loading draft:', error);
      }
    }
  }, []);

  // Auto-save when form data changes
  useEffect(() => {
    const timeoutId = setTimeout(saveDraft, 2000);
    return () => clearTimeout(timeoutId);
  }, [newCase, selectedCategory, selectedSubcategory, activeStep]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.ctrlKey || e.metaKey) {
        switch (e.key) {
          case 's':
            e.preventDefault();
            saveDraft();
            break;
          case 'Enter':
            e.preventDefault();
            if (activeStep < 2 && selectedCategory) {
              setActiveStep(activeStep + 1);
            } else if (activeStep === 2 && newCase.title.trim() && newCase.client.trim() && newCase.case_number.trim() && newCase.party_1.trim()) {
              handleSaveCase();
            }
            break;
        }
      } else if (e.key === 'Escape') {
        navigate('/dashboard');
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [activeStep, selectedCategory, newCase]);

  useEffect(() => {
    // Simulate API call to fetch categories
    setTimeout(() => {
      setCategories(mockCategories);
    }, 1000);

    // Mock data
    setStats({
      totalCases: 25,
      activeCases: 12,
      totalClients: 18,
      totalRequests: 8,
      totalMatters: 15,
      recentActivity: 5,
    });

    setRecentCases([
      {
        _id: '1',
        title: 'Property Dispute Case',
        client: 'Ahmed Khan',
        status: 'In Progress',
        date: '2024-01-15',
        category: 'Civil Law',
      },
      {
        _id: '2',
        title: 'Employment Contract Review',
        client: 'Sarah Ahmed',
        status: 'Completed',
        date: '2024-01-10',
        category: 'Employment Law',
      },
      {
        _id: '3',
        title: 'Business Registration',
        client: 'Muhammad Hassan',
        status: 'Pending',
        date: '2024-01-08',
        category: 'Corporate Law',
      },
    ]);
  }, []);

  const handleAddNewCase = () => {
    setOpenCaseDialog(true);
    setActiveStep(0);
    setSelectedCategory(null);
    setSelectedSubcategory(null);
    setNewCase({ title: '', client: '', case_number: '', party_1: '', case_description: '' });
  };

  const handleCloseCaseDialog = () => {
    setOpenCaseDialog(false);
    setActiveStep(0);
    setSelectedCategory(null);
    setSelectedSubcategory(null);
    setNewCase({ title: '', client: '', case_number: '', party_1: '', case_description: '' });
  };

  const handleCategorySelect = (category: CaseCategory) => {
    setSelectedCategory(category);
    setActiveStep(1);
  };

  const handleSubcategorySelect = (subcategory: CaseSubcategory) => {
    setSelectedSubcategory(subcategory);
    setActiveStep(2);
  };

  const handleSaveCase = () => {
    console.log('Saving case:', {
      category_id: selectedCategory?._id,
      subcategory_id: selectedSubcategory?._id,
      title: newCase.title,
      client: newCase.client,
      case_number: newCase.case_number,
      party_1: newCase.party_1,
      case_description: newCase.case_description,
    });

    // Clear draft after successful submission
    localStorage.removeItem('case-draft');

    handleCloseCaseDialog();
    navigate('/cases');
  };

  const handleQuickAction = (action: string) => {
    switch (action) {
      case 'addCase':
        handleAddNewCase();
        break;
      case 'clientRequests':
        navigate('/client-requests');
        break;
      case 'clientMatters':
        navigate('/lawyer-matters');
        break;
      case 'scheduleHearing':
        navigate('/calendar');
        break;
      case 'sendMessage':
        navigate('/chat');
        break;
      case 'uploadDocument':
        navigate('/documents');
        break;
      default:
        break;
    }
  };

  const steps = ['Select Category', 'Select Subcategory', 'Case Details'];

  const getCategoryIcon = (iconName: string) => {
    const iconMap: { [key: string]: React.ReactNode } = {
      family: <People />,
      civil: <Description />,
      employment: <Assignment />,
      criminal: <Gavel />,
      corporate: <Description />,
      banking: <Description />,
      taxation: <Description />,
      property: <Description />,
    };
    return iconMap[iconName] || <Assignment />;
  };

  return (
    <Box>
      {/* Breadcrumbs */}
      <Breadcrumbs items={[
        { label: 'Dashboard', current: true }
      ]} />

      <Typography variant="h4" component="h1" gutterBottom fontWeight="bold">
        Welcome back, {user?.first_name}!
      </Typography>

      {/* Statistics Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ backgroundColor: Colors.primary, color: 'white' }}>
            <CardContent>
              <Typography variant="h4" fontWeight="bold">{stats.totalCases}</Typography>
              <Typography variant="body2">Total Cases</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ backgroundColor: '#EAA141', color: 'white' }}>
            <CardContent>
              <Typography variant="h4" fontWeight="bold">{stats.activeCases}</Typography>
              <Typography variant="body2">Active Cases</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ backgroundColor: '#5491C9', color: 'white' }}>
            <CardContent>
              <Typography variant="h4" fontWeight="bold">{stats.totalClients}</Typography>
              <Typography variant="body2">Total Clients</Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card sx={{ backgroundColor: '#4CAF50', color: 'white' }}>
            <CardContent>
              <Typography variant="h4" fontWeight="bold">{stats.totalRequests}</Typography>
              <Typography variant="body2">Client Requests</Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Quick Actions Section - Matching XD Design */}
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Typography variant="h5" fontWeight="bold" gutterBottom>Quick Actions</Typography>
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6} md={3}>
              <Card onClick={() => handleQuickAction('addCase')} sx={{ cursor: 'pointer', backgroundColor: Colors.primary, color: 'white', '&:hover': { backgroundColor: Colors.primaryDark } }}>
                <CardContent sx={{ textAlign: 'center', py: 3 }}>
                  <Add sx={{ fontSize: 48, color: 'white', mb: 2 }} />
                  <Typography variant="h6" fontWeight="bold">Add New Case</Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Card onClick={() => handleQuickAction('clientRequests')} sx={{ cursor: 'pointer', backgroundColor: '#EAA141', color: 'white', '&:hover': { backgroundColor: '#D8943A' } }}>
                <CardContent sx={{ textAlign: 'center', py: 3 }}>
                  <Assignment sx={{ fontSize: 48, color: 'white', mb: 2 }} />
                  <Typography variant="h6" fontWeight="bold">Client Requests</Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Card onClick={() => handleQuickAction('clientMatters')} sx={{ cursor: 'pointer', backgroundColor: '#5491C9', color: 'white', '&:hover': { backgroundColor: '#4A7FB8' } }}>
                <CardContent sx={{ textAlign: 'center', py: 3 }}>
                  <Gavel sx={{ fontSize: 48, color: 'white', mb: 2 }} />
                  <Typography variant="h6" fontWeight="bold">Client Matters</Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Card onClick={() => handleQuickAction('scheduleHearing')} sx={{ cursor: 'pointer', backgroundColor: '#4CAF50', color: 'white', '&:hover': { backgroundColor: '#45A049' } }}>
                <CardContent sx={{ textAlign: 'center', py: 3 }}>
                  <CalendarToday sx={{ fontSize: 48, color: 'white', mb: 2 }} />
                  <Typography variant="h6" fontWeight="bold">Schedule Hearing</Typography>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      <Grid container spacing={3}>
        <Grid item xs={12} lg={8}>
          <Card>
            <CardContent>
              <Typography variant="h6" fontWeight="bold" gutterBottom>Recent Cases</Typography>
              <List>
                {recentCases.map((case_, index) => (
                  <React.Fragment key={case_._id}>
                    <ListItem>
                      <ListItemAvatar>
                        <Avatar sx={{ backgroundColor: Colors.primary }}>
                          <Gavel />
                        </Avatar>
                      </ListItemAvatar>
                      <ListItemText
                        primary={case_.title}
                        secondary={
                          <Box>
                            <Typography variant="body2" color="textSecondary">
                              Client: {case_.client} • {case_.category} • {case_.date}
                            </Typography>
                          </Box>
                        }
                      />
                      <Chip
                        label={case_.status}
                        color={
                          case_.status === 'Completed' ? 'success' :
                          case_.status === 'In Progress' ? 'primary' : 'default'
                        }
                        size="small"
                      />
                    </ListItem>
                    {index < recentCases.length - 1 && <Divider />}
                  </React.Fragment>
                ))}
              </List>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} lg={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" fontWeight="bold" gutterBottom>Additional Actions</Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                <Button variant="outlined" fullWidth color="primary" startIcon={<Description />} onClick={() => handleQuickAction('uploadDocument')}>Upload Document</Button>
                <Button variant="outlined" fullWidth color="primary" startIcon={<PersonAdd />} onClick={() => navigate('/clients')}>Add New Client</Button>
                <Button variant="outlined" fullWidth color="primary" startIcon={<People />} onClick={() => navigate('/team')}>Team Management</Button>
                <Button variant="outlined" fullWidth color="primary" startIcon={<Assignment />} onClick={() => navigate('/matters')}>View Matters</Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Add New Case Dialog */}
      <Dialog open={openCaseDialog} onClose={handleCloseCaseDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          <Box sx={{ mb: 2 }}>
            <Typography variant="h6" fontWeight="bold">Add New Case</Typography>
            <Stepper activeStep={activeStep} sx={{ mt: 2 }}>
              {steps.map((label) => (
                <Step key={label}>
                  <StepLabel>{label}</StepLabel>
                </Step>
              ))}
            </Stepper>
          </Box>
        </DialogTitle>
        <DialogContent>
          {/* Draft saved notification */}
          {showDraftSaved && (
            <Alert 
              severity="success" 
              sx={{ mb: 2 }}
              action={
                <Button color="inherit" size="small" onClick={() => setShowDraftSaved(false)}>
                  Dismiss
                </Button>
              }
            >
              Draft saved automatically
            </Alert>
          )}

          {activeStep === 0 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Select Category
              </Typography>

              <Grid container spacing={2}>
                {categories.map((category) => (
                  <Grid item xs={6} sm={4} md={3} key={category._id}>
                    <Card
                      sx={{
                        cursor: 'pointer',
                        border: selectedCategory?._id === category._id ? `2px solid #FFA500` : '1px solid #D1D3D4',
                        borderRadius: 3,
                        backgroundColor: selectedCategory?._id === category._id ? '#FFA500' : 'white',
                        color: selectedCategory?._id === category._id ? 'white' : 'inherit',
                        '&:hover': { borderColor: '#FFA500' },
                        height: 120,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        justifyContent: 'center',
                      }}
                      onClick={() => handleCategorySelect(category)}
                    >
                      <Box sx={{ color: selectedCategory?._id === category._id ? 'white' : Colors.primary, mb: 1 }}>
                        {getCategoryIcon(category.icon)}
                      </Box>
                      <Typography variant="body2" textAlign="center" fontWeight="medium">
                        {category.name}
                      </Typography>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {activeStep === 1 && selectedCategory && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Select Subcategory
              </Typography>

              <FormControl component="fieldset" sx={{ width: '100%' }}>
                <RadioGroup value={selectedSubcategory?._id || ''}>
                  {mockSubcategories[selectedCategory._id]?.map((subcategory) => (
                    <FormControlLabel
                      key={subcategory._id}
                      value={subcategory._id}
                      control={<Radio />}
                      label={subcategory.name}
                      onClick={() => handleSubcategorySelect(subcategory)}
                      sx={{
                        border: '1px solid #D1D3D4',
                        borderRadius: 2,
                        margin: '8px 0',
                        padding: '12px 16px',
                        width: '100%',
                        backgroundColor: selectedSubcategory?._id === subcategory._id ? '#FFA500' : 'white',
                        color: selectedSubcategory?._id === subcategory._id ? 'white' : 'inherit',
                        '&:hover': {
                          backgroundColor: selectedSubcategory?._id === subcategory._id ? '#FFA500' : '#f5f5f5',
                        },
                        '& .MuiFormControlLabel-label': {
                          color: selectedSubcategory?._id === subcategory._id ? 'white' : 'inherit',
                          fontWeight: selectedSubcategory?._id === subcategory._id ? 600 : 400,
                        },
                      }}
                    />
                  ))}
                </RadioGroup>
              </FormControl>
            </Box>
          )}

          {activeStep === 2 && (
            <Box sx={{ pt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
              <Typography variant="h6" gutterBottom>Case Details - {selectedCategory?.name} / {selectedSubcategory?.name}</Typography>
              <TextField
                fullWidth
                label="Case Title"
                value={newCase.title}
                onChange={(e) => setNewCase({ ...newCase, title: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Client Name"
                value={newCase.client}
                onChange={(e) => setNewCase({ ...newCase, client: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Case Number"
                value={newCase.case_number}
                onChange={(e) => setNewCase({ ...newCase, case_number: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Party 1 (Opposing Party)"
                value={newCase.party_1}
                onChange={(e) => setNewCase({ ...newCase, party_1: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Case Description"
                value={newCase.case_description}
                onChange={(e) => setNewCase({ ...newCase, case_description: e.target.value })}
                multiline
                rows={4}
                helperText={`${newCase.case_description.length}/1000 characters`}
                error={newCase.case_description.length > 1000}
              />
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseCaseDialog}>Cancel</Button>
          {activeStep > 0 && (
            <Button onClick={() => setActiveStep(activeStep - 1)}>Back</Button>
          )}
          {activeStep < 2 && selectedCategory && (
            <Button variant="contained" onClick={() => setActiveStep(activeStep + 1)} sx={{ backgroundColor: Colors.primary }}>
              Next
            </Button>
          )}
          {activeStep === 2 && (
            <Button
              variant="contained"
              onClick={handleSaveCase}
              disabled={!newCase.title || !newCase.client || !newCase.case_number || !newCase.party_1 || newCase.case_description.length > 1000}
              sx={{ backgroundColor: Colors.primary }}
            >
              Save Case
            </Button>
          )}
        </DialogActions>
      </Dialog>

      {/* Keyboard shortcuts hint */}
      <Typography variant="caption" color="textSecondary" sx={{ display: 'block', textAlign: 'center', mt: 4 }}>
        💡 Tip: Press Ctrl+S to save draft, Ctrl+Enter to proceed
      </Typography>
    </Box>
  );
};

export default Dashboard;
