import React, { useState } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  TextField,
} from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';

const AddRequest3: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { service_id, serviceName, type } = location.state || {};

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = () => {
    if (!title.trim()) {
      alert('Title is required');
      return;
    }
    if (!description.trim()) {
      alert('Description is required');
      return;
    }

    // Submit to API - exact same payload as Android app
    console.log('Submitting request:', {
      service_id: service_id,
      request_type: type,
      title: title,
      description: description,
      sub_service_id: null
    });

    // Navigate back to dashboard
    navigate('/my-requests');
  };

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom fontWeight="bold">
        Paralegal Assistance
      </Typography>

      <Card>
        <CardContent>
          <Box sx={{ mb: 4 }}>
            <Typography variant="h6" gutterBottom>
              Describe Your Need
            </Typography>
            <Typography variant="subtitle2" color="textSecondary" gutterBottom>
              Selected Document
            </Typography>
            <Typography variant="body1" fontWeight="medium" gutterBottom>
              {serviceName}
            </Typography>
          </Box>

          <TextField
            fullWidth
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            sx={{
              mb: 3,
              '& .MuiOutlinedInput-root': {
                borderRadius: 2,
                backgroundColor: 'white',
              },
            }}
          />

          <TextField
            fullWidth
            placeholder="Describe your requirements"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            multiline
            rows={8}
            sx={{
              mb: 3,
              '& .MuiOutlinedInput-root': {
                borderRadius: 2,
                backgroundColor: 'white',
              },
            }}
          />

          <Button
            variant="contained"
            fullWidth
            disabled={!title.trim() || !description.trim()}
            onClick={handleSubmit}
            sx={{
              backgroundColor: (title.trim() && description.trim()) ? '#FFA500' : '#D3D3D3',
              borderRadius: 2,
              height: 50,
            }}
          >
            Submit
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
};

export default AddRequest3;
