import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Chip,
  LinearProgress,
  Paper,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stepper,
  Step,
  StepLabel,
} from '@mui/material';
import {
  TrendingUp,
  People,
  Assignment,
  Chat,
  CalendarToday,
  Notifications,
} from '@mui/icons-material';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';

interface DashboardStats {
  totalCases: number;
  activeCases: number;
  totalClients: number;
  upcomingHearings: number;
  pendingTasks: number;
  recentMessages: number;
}

interface RecentCase {
  id: string;
  title: string;
  client: string;
  status: string;
  nextHearing: string;
  progress: number;
}

interface CaseCategory {
  _id: string;
  name: string;
  icon?: string;
  subcategories: CaseSubcategory[];
}

interface CaseSubcategory {
  _id: string;
  name: string;
  icon?: string;
}

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState<DashboardStats>({
    totalCases: 0,
    activeCases: 0,
    totalClients: 0,
    upcomingHearings: 0,
    pendingTasks: 0,
    recentMessages: 0,
  });

  const [recentCases, setRecentCases] = useState<RecentCase[]>([]);
  const [openCaseDialog, setOpenCaseDialog] = useState(false);
  const [activeStep, setActiveStep] = useState(0);
  const [categories, setCategories] = useState<CaseCategory[]>([]);
  const [selectedCategory, setSelectedCategory] = useState<CaseCategory | null>(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState<CaseSubcategory | null>(null);
  const [newCase, setNewCase] = useState({
    title: '',
    client: '',
    case_number: '',
    party_1: '',
    case_description: '',
  });

  // Mock categories based on typical legal practice categories
  const mockCategories: CaseCategory[] = [
    {
      _id: '1',
      name: 'Criminal Law',
      subcategories: [
        { _id: '1-1', name: 'Murder' },
        { _id: '1-2', name: 'Theft' },
        { _id: '1-3', name: 'Assault' },
        { _id: '1-4', name: 'Drug Offenses' },
        { _id: '1-5', name: 'Fraud' },
        { _id: '1-6', name: 'Cyber Crime' },
      ]
    },
    {
      _id: '2',
      name: 'Civil Law',
      subcategories: [
        { _id: '2-1', name: 'Contract Disputes' },
        { _id: '2-2', name: 'Property Disputes' },
        { _id: '2-3', name: 'Tort Claims' },
        { _id: '2-4', name: 'Landlord-Tenant' },
        { _id: '2-5', name: 'Personal Injury' },
        { _id: '2-6', name: 'Medical Malpractice' },
      ]
    },
    {
      _id: '3',
      name: 'Family Law',
      subcategories: [
        { _id: '3-1', name: 'Divorce' },
        { _id: '3-2', name: 'Child Custody' },
        { _id: '3-3', name: 'Child Support' },
        { _id: '3-4', name: 'Adoption' },
        { _id: '3-5', name: 'Marriage Registration' },
        { _id: '3-6', name: 'Domestic Violence' },
      ]
    },
    {
      _id: '4',
      name: 'Corporate Law',
      subcategories: [
        { _id: '4-1', name: 'Company Registration' },
        { _id: '4-2', name: 'Corporate Governance' },
        { _id: '4-3', name: 'Mergers & Acquisitions' },
        { _id: '4-4', name: 'Intellectual Property' },
        { _id: '4-5', name: 'Employment Law' },
        { _id: '4-6', name: 'Tax Law' },
      ]
    },
    {
      _id: '5',
      name: 'Property Law',
      subcategories: [
        { _id: '5-1', name: 'Real Estate' },
        { _id: '5-2', name: 'Property Registration' },
        { _id: '5-3', name: 'Land Disputes' },
        { _id: '5-4', name: 'Property Tax' },
        { _id: '5-5', name: 'Rental Agreements' },
        { _id: '5-6', name: 'Property Development' },
      ]
    },
    {
      _id: '6',
      name: 'Constitutional Law',
      subcategories: [
        { _id: '6-1', name: 'Fundamental Rights' },
        { _id: '6-2', name: 'Public Interest Litigation' },
        { _id: '6-3', name: 'Administrative Law' },
        { _id: '6-4', name: 'Election Law' },
        { _id: '6-5', name: 'Human Rights' },
        { _id: '6-6', name: 'Judicial Review' },
      ]
    },
    {
      _id: '7',
      name: 'Banking & Finance',
      subcategories: [
        { _id: '7-1', name: 'Banking Regulations' },
        { _id: '7-2', name: 'Securities Law' },
        { _id: '7-3', name: 'Insurance Law' },
        { _id: '7-4', name: 'Debt Recovery' },
        { _id: '7-5', name: 'Financial Fraud' },
        { _id: '7-6', name: 'Investment Law' },
      ]
    },
    {
      _id: '8',
      name: 'Labor Law',
      subcategories: [
        { _id: '8-1', name: 'Employment Contracts' },
        { _id: '8-2', name: 'Workplace Safety' },
        { _id: '8-3', name: 'Industrial Disputes' },
        { _id: '8-4', name: 'Workers Compensation' },
        { _id: '8-5', name: 'Trade Unions' },
        { _id: '8-6', name: 'Minimum Wage' },
      ]
    }
  ];

  useEffect(() => {
    // TODO: Fetch dashboard data from API
    setStats({
      totalCases: 24,
      activeCases: 18,
      totalClients: 12,
      upcomingHearings: 5,
      pendingTasks: 8,
      recentMessages: 3,
    });

    setRecentCases([
      {
        id: '1',
        title: 'Criminal Case vs. State',
        client: 'Ahmed Khan',
        status: 'Active',
        nextHearing: '2024-02-15',
        progress: 75,
      },
      {
        id: '2',
        title: 'Civil Suit - Property Dispute',
        client: 'Fatima Ali',
        status: 'Pending',
        nextHearing: '2024-02-20',
        progress: 45,
      },
      {
        id: '3',
        title: 'Family Law - Divorce',
        client: 'Muhammad Hassan',
        status: 'Active',
        nextHearing: '2024-02-18',
        progress: 60,
      },
    ]);

    setCategories(mockCategories);
  }, []);

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'active':
        return Colors.success;
      case 'pending':
        return Colors.warning;
      case 'closed':
        return Colors.error;
      default:
        return Colors.gray500;
    }
  };

  const handleAddNewCase = () => {
    setOpenCaseDialog(true);
    setActiveStep(0);
    setSelectedCategory(null);
    setSelectedSubcategory(null);
  };

  const handleCloseCaseDialog = () => {
    setOpenCaseDialog(false);
    setActiveStep(0);
    setSelectedCategory(null);
    setSelectedSubcategory(null);
    setNewCase({ title: '', client: '', case_number: '', party_1: '', case_description: '' });
  };

  const handleCategorySelect = (category: CaseCategory) => {
    setSelectedCategory(category);
    setActiveStep(1);
  };

  const handleSubcategorySelect = (subcategory: CaseSubcategory) => {
    setSelectedSubcategory(subcategory);
    setActiveStep(2);
  };

  const handleSaveCase = () => {
    // TODO: Save case to API
    const caseToAdd: RecentCase = {
      id: Date.now().toString(),
      title: newCase.title,
      client: newCase.client,
      status: 'Active',
      nextHearing: new Date().toISOString().split('T')[0],
      progress: 0,
    };

    setRecentCases([caseToAdd, ...recentCases]);
    setStats(prev => ({
      ...prev,
      totalCases: prev.totalCases + 1,
      activeCases: prev.activeCases + 1,
    }));

    handleCloseCaseDialog();
  };

  const handleQuickAction = (action: string) => {
    switch (action) {
      case 'addCase':
        handleAddNewCase();
        break;
      case 'scheduleHearing':
        navigate('/calendar');
        break;
      case 'sendMessage':
        navigate('/chat');
        break;
      case 'uploadDocument':
        navigate('/documents');
        break;
      default:
        break;
    }
  };

  const StatCard: React.FC<{
    title: string;
    value: number;
    icon: React.ReactNode;
    color: string;
  }> = ({ title, value, icon, color }) => (
    <Card sx={{ height: '100%' }}>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Box>
            <Typography color="textSecondary" gutterBottom variant="body2">
              {title}
            </Typography>
            <Typography variant="h4" component="div" fontWeight="bold" color={color}>
              {value}
            </Typography>
          </Box>
          <Avatar sx={{ bgcolor: color, width: 56, height: 56 }}>
            {icon}
          </Avatar>
        </Box>
      </CardContent>
    </Card>
  );

  const steps = ['Select Category', 'Select Subcategory', 'Case Details'];

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom fontWeight="bold">
        Welcome back, {user?.first_name}!
      </Typography>

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Total Cases"
            value={stats.totalCases}
            icon={<Assignment />}
            color={Colors.primary}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Active Cases"
            value={stats.activeCases}
            icon={<TrendingUp />}
            color={Colors.success}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Total Clients"
            value={stats.totalClients}
            icon={<People />}
            color={Colors.secondary}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Upcoming Hearings"
            value={stats.upcomingHearings}
            icon={<CalendarToday />}
            color={Colors.warning}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Pending Tasks"
            value={stats.pendingTasks}
            icon={<Notifications />}
            color={Colors.error}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Recent Messages"
            value={stats.recentMessages}
            icon={<Chat />}
            color={Colors.info}
          />
        </Grid>
      </Grid>

      <Grid container spacing={3}>
        <Grid item xs={12} lg={8}>
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                <Typography variant="h6" fontWeight="bold">
                  Recent Cases
                </Typography>
                <Button variant="outlined" color="primary" onClick={() => navigate('/cases')}>
                  View All
                </Button>
              </Box>

              <List>
                {recentCases.map((caseItem, index) => (
                  <React.Fragment key={caseItem.id}>
                    <ListItem alignItems="flex-start">
                      <ListItemAvatar>
                        <Avatar sx={{ bgcolor: Colors.primary }}>
                          {index + 1}
                        </Avatar>
                      </ListItemAvatar>
                      <ListItemText
                        primary={
                          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <Typography variant="subtitle1" fontWeight="bold">
                              {caseItem.title}
                            </Typography>
                            <Chip
                              label={caseItem.status}
                              size="small"
                              sx={{
                                backgroundColor: getStatusColor(caseItem.status),
                                color: 'white',
                              }}
                            />
                          </Box>
                        }
                        secondary={
                          <Box>
                            <Typography variant="body2" color="textSecondary">
                              Client: {caseItem.client}
                            </Typography>
                            <Typography variant="body2" color="textSecondary">
                              Next Hearing: {caseItem.nextHearing}
                            </Typography>
                            <Box sx={{ mt: 1 }}>
                              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
                                <Typography variant="body2" color="textSecondary">
                                  Progress
                                </Typography>
                                <Typography variant="body2" color="textSecondary">
                                  {caseItem.progress}%
                                </Typography>
                              </Box>
                              <LinearProgress
                                variant="determinate"
                                value={caseItem.progress}
                                sx={{
                                  height: 6,
                                  borderRadius: 3,
                                  backgroundColor: Colors.gray200,
                                  '& .MuiLinearProgress-bar': {
                                    backgroundColor: Colors.primary,
                                  },
                                }}
                              />
                            </Box>
                          </Box>
                        }
                      />
                    </ListItem>
                    {index < recentCases.length - 1 && <Divider variant="inset" component="li" />}
                  </React.Fragment>
                ))}
              </List>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} lg={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" fontWeight="bold" gutterBottom>
                Quick Actions
              </Typography>

              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                <Button
                  variant="contained"
                  fullWidth
                  sx={{ backgroundColor: Colors.primary }}
                  onClick={() => handleQuickAction('addCase')}
                >
                  Add New Case
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  onClick={() => handleQuickAction('clientRequests')}
                >
                  Client Requests
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  onClick={() => handleQuickAction('scheduleHearing')}
                >
                  Schedule Hearing
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  onClick={() => handleQuickAction('sendMessage')}
                >
                  Send Message
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  onClick={() => handleQuickAction('uploadDocument')}
                >
                  Upload Document
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Add New Case Dialog */}
      <Dialog open={openCaseDialog} onClose={handleCloseCaseDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          <Box sx={{ mb: 2 }}>
            <Typography variant="h6" fontWeight="bold">
              Add New Case
            </Typography>
            <Stepper activeStep={activeStep} sx={{ mt: 2 }}>
              {steps.map((label) => (
                <Step key={label}>
                  <StepLabel>{label}</StepLabel>
                </Step>
              ))}
            </Stepper>
          </Box>
        </DialogTitle>
        <DialogContent>
          {activeStep === 0 && (
            <Box sx={{ pt: 2 }}>
              <Typography variant="h6" gutterBottom>
                Select Case Category
              </Typography>
              <Grid container spacing={2}>
                {categories.map((category) => (
                  <Grid item xs={12} sm={6} key={category._id}>
                    <Card
                      sx={{
                        cursor: 'pointer',
                        border: selectedCategory?._id === category._id ? `2px solid ${Colors.primary}` : '1px solid #e0e0e0',
                        '&:hover': { borderColor: Colors.primary },
                      }}
                      onClick={() => handleCategorySelect(category)}
                    >
                      <CardContent sx={{ textAlign: 'center', py: 3 }}>
                        <Typography variant="h6" fontWeight="bold">
                          {category.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          {category.subcategories.length} subcategories
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {activeStep === 1 && selectedCategory && (
            <Box sx={{ pt: 2 }}>
              <Typography variant="h6" gutterBottom>
                Select Subcategory - {selectedCategory.name}
              </Typography>
              <Grid container spacing={2}>
                {selectedCategory.subcategories.map((subcategory) => (
                  <Grid item xs={12} sm={6} key={subcategory._id}>
                    <Card
                      sx={{
                        cursor: 'pointer',
                        border: selectedSubcategory?._id === subcategory._id ? `2px solid ${Colors.primary}` : '1px solid #e0e0e0',
                        '&:hover': { borderColor: Colors.primary },
                      }}
                      onClick={() => handleSubcategorySelect(subcategory)}
                    >
                      <CardContent sx={{ textAlign: 'center', py: 2 }}>
                        <Typography variant="subtitle1" fontWeight="medium">
                          {subcategory.name}
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {activeStep === 2 && (
            <Box sx={{ pt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
              <Typography variant="h6" gutterBottom>
                Case Details - {selectedCategory?.name} / {selectedSubcategory?.name}
              </Typography>
              <TextField
                fullWidth
                label="Case Title"
                value={newCase.title}
                onChange={(e) => setNewCase({ ...newCase, title: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Client Name"
                value={newCase.client}
                onChange={(e) => setNewCase({ ...newCase, client: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Case Number"
                value={newCase.case_number}
                onChange={(e) => setNewCase({ ...newCase, case_number: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Party 1 (Opposing Party)"
                value={newCase.party_1}
                onChange={(e) => setNewCase({ ...newCase, party_1: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Case Description"
                value={newCase.case_description}
                onChange={(e) => setNewCase({ ...newCase, case_description: e.target.value })}
                multiline
                rows={4}
              />
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseCaseDialog}>Cancel</Button>
          {activeStep > 0 && (
            <Button onClick={() => setActiveStep(activeStep - 1)}>
              Back
            </Button>
          )}
          {activeStep < 2 && selectedCategory && (
            <Button 
              variant="contained" 
              onClick={() => setActiveStep(activeStep + 1)}
              sx={{ backgroundColor: Colors.primary }}
            >
              Next
            </Button>
          )}
          {activeStep === 2 && (
            <Button 
              variant="contained" 
              onClick={handleSaveCase}
              disabled={!newCase.title || !newCase.client || !newCase.case_number || !newCase.party_1}
              sx={{ backgroundColor: Colors.primary }}
            >
              Save Case
            </Button>
          )}
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Dashboard;
