      { _id: '8-6', name: 'Property Development', icon: 'development', parent: '8' },
    ],
  };

  // Enhanced auto-save with better feedback
  const saveDraft = () => {
    const draft = {
      title,
      description,
      selectedCategory: selectedCategory?._id,
      selectedSubcategory: selectedSubcategory?._id,
      activeStep,
      timestamp: new Date().toISOString(),
    };
    localStorage.setItem('matter-draft', JSON.stringify(draft));
    setSnackbar({
      open: true,
      message: 'Draft saved automatically',
      severity: 'success'
    });
  };

  // Load draft on component mount
  useEffect(() => {
    const savedDraft = localStorage.getItem('matter-draft');
    if (savedDraft) {
      try {
        const draft = JSON.parse(savedDraft);
        setTitle(draft.title || '');
        setDescription(draft.description || '');
        setActiveStep(draft.activeStep || 0);

        // Restore category and subcategory selections
        if (draft.selectedCategory) {
          const category = mockCategories.find(c => c._id === draft.selectedCategory);
          if (category) {
            setSelectedCategory(category);
            setSubcategories(mockSubcategories[category._id] || []);
          }
        }

        if (draft.selectedSubcategory) {
          const subcategory = subcategories.find(s => s._id === draft.selectedSubcategory);
          if (subcategory) {
            setSelectedSubcategory(subcategory);
          }
        }
      } catch (error) {
        console.error('Error loading draft:', error);
      }
    }
  }, []);

  // Auto-save when form data changes
  useEffect(() => {
    const timeoutId = setTimeout(saveDraft, 2000);
    return () => clearTimeout(timeoutId);
  }, [title, description, selectedCategory, selectedSubcategory, activeStep]);

  // Enhanced keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.ctrlKey || e.metaKey) {
        switch (e.key) {
          case 's':
            e.preventDefault();
            saveDraft();
            break;
          case 'Enter':
            e.preventDefault();
            if (activeStep < 2 && selectedCategory) {
              handleNext();
            } else if (activeStep === 2 && title.trim() && description.trim()) {
              setShowSubmitDialog(true);
            }
            break;
        }
      } else if (e.key === 'Escape') {
        if (title.trim() || description.trim() || selectedCategory || selectedSubcategory) {
          setShowExitDialog(true);
        } else {
          navigate('/dashboard');
        }
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [activeStep, selectedCategory, title, description]);

  useEffect(() => {
    // Simulate API call to fetch categories
    setTimeout(() => {
      setCategories(mockCategories);
      setLoading(false);
    }, 1000);
  }, []);

  const handleCategorySelect = (category: MatterCategory) => {
    setSelectedCategory(category);
    setSubcategories(mockSubcategories[category._id] || []);
    handleNext();
  };

  const handleSubcategorySelect = (subcategory: MatterSubcategory) => {
    setSelectedSubcategory(subcategory);
    handleNext();
  };

  const handleNext = () => {
    if (activeStep < 2) {
      setActiveStep(activeStep + 1);
    }
  };

  const handleBack = () => {
    if (activeStep > 0) {
      setActiveStep(activeStep - 1);
    } else {
      if (title.trim() || description.trim() || selectedCategory || selectedSubcategory) {
        setShowExitDialog(true);
      } else {
        navigate('/dashboard');
      }
    }
  };

  const handleSubmit = async () => {
    setSubmitting(true);
    setShowSubmitDialog(false);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Submit to API - exact same payload as Android app
      console.log('Submitting matter:', {
        category_id: selectedCategory?._id,
        subcategory_id: selectedSubcategory?._id,
        title,
        description,
      });

      // Clear draft after successful submission
      localStorage.removeItem('matter-draft');

      setShowSuccessDialog(true);
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to submit matter. Please try again.',
        severity: 'error'
      });
    } finally {
      setSubmitting(false);
    }
  };

  const handleSuccessClose = () => {
    setShowSuccessDialog(false);
    navigate('/my-matters');
  };

  const getCategoryIcon = (iconName: string) => {
    const iconMap: { [key: string]: React.ReactNode } = {
      family: <Home />,
      civil: <Description />,
      employment: <Work />,
      criminal: <Gavel />,
      corporate: <Business />,
      banking: <AccountBalance />,
      taxation: <School />,
      property: <Home />,
    };
    return iconMap[iconName] || <Assignment />;
  };

