              sx={{ 
                minWidth: isMobile ? '100%' : 'auto',
                order: isMobile ? 2 : 1
              }}
            >
              {activeStep === 0 ? 'Back to Dashboard' : 'Back'}
            </Button>

            {/* Keyboard shortcuts hint */}
            <Typography 
              variant="caption" 
              color="textSecondary" 
              sx={{ 
                alignSelf: 'center',
                order: isMobile ? 1 : 2,
                textAlign: isMobile ? 'center' : 'left'
              }}
            >
