        <DialogTitle sx={{ pb: 1, textAlign: 'center' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mb: 2 }}>
            <CheckCircle sx={{ fontSize: 60, color: 'success.main', mr: 2 }} />
