import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Chip,
  IconButton,
  TextField,
  InputAdornment,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
} from '@mui/material';
import {
  Search,
  Add,
  Edit,
  Delete,
  Visibility,
  FilterList,
} from '@mui/icons-material';
import { Colors } from '../assets/styles/colors';

interface Case {
  id: string;
  title: string;
  caseNumber: string;
  client: string;
  category: string;
  status: string;
  nextHearing: string;
  lawyer: string;
  progress: number;
}

const Cases: React.FC = () => {
  const [cases, setCases] = useState<Case[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedCase, setSelectedCase] = useState<Case | null>(null);

  useEffect(() => {
    // TODO: Fetch cases from API
    setCases([
      {
        id: '1',
        title: 'Criminal Case vs. State',
        caseNumber: 'CR-2024-001',
        client: 'Ahmed Khan',
        category: 'Criminal',
        status: 'Active',
        nextHearing: '2024-02-15',
        lawyer: 'Adv. Sarah Ahmed',
        progress: 75,
      },
      {
        id: '2',
        title: 'Civil Suit - Property Dispute',
        caseNumber: 'CS-2024-002',
        client: 'Fatima Ali',
        category: 'Civil',
        status: 'Pending',
        nextHearing: '2024-02-20',
        lawyer: 'Adv. Muhammad Hassan',
        progress: 45,
      },
      {
        id: '3',
        title: 'Family Law - Divorce',
        caseNumber: 'FL-2024-003',
        client: 'Muhammad Hassan',
        category: 'Family',
        status: 'Active',
        nextHearing: '2024-02-18',
        lawyer: 'Adv. Ayesha Khan',
        progress: 60,
      },
    ]);
  }, []);

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'active':
        return Colors.success;
      case 'pending':
        return Colors.warning;
      case 'closed':
        return Colors.error;
      default:
        return Colors.gray500;
    }
  };

  const getCategoryColor = (category: string) => {
    switch (category.toLowerCase()) {
      case 'criminal':
        return Colors.error;
      case 'civil':
        return Colors.primary;
      case 'family':
        return Colors.secondary;
      default:
        return Colors.gray500;
    }
  };

  const filteredCases = cases.filter((caseItem) => {
    const matchesSearch = caseItem.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         caseItem.caseNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         caseItem.client.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || caseItem.status.toLowerCase() === statusFilter.toLowerCase();
    return matchesSearch && matchesStatus;
  });

  const handleEdit = (caseItem: Case) => {
    setSelectedCase(caseItem);
    setOpenDialog(true);
  };

  const handleDelete = (id: string) => {
    // TODO: Implement delete functionality
