      { _id: '4-3', name: 'Assault', icon: 'assault', parent: '4' },
      { _id: '4-4', name: 'Drug Offenses', icon: 'drugs', parent: '4' },
      { _id: '4-5', name: 'Fraud', icon: 'fraud', parent: '4' },
