  }, []);

  const handleCategorySelect = (category: MatterCategory) => {
    setSelectedCategory(category);
    setSubcategories(mockSubcategories[category._id] || []);
    setActiveStep(1);
  };

  const handleSubcategorySelect = (subcategory: MatterSubcategory) => {
    setSelectedSubcategory(subcategory);
    setActiveStep(2);
  };

  const handleSubmit = () => {
    // Submit to API - exact same payload as Android app
    console.log('Submitting matter:', {
      category_id: selectedCategory?._id,
      subcategory_id: selectedSubcategory?._id,
      title,
      description,
    });

    // Navigate back to dashboard
    navigate('/my-matters');
  };

  const getCategoryIcon = (iconName: string) => {
    const iconMap: { [key: string]: React.ReactNode } = {
      family: <Home />,
      civil: <Description />,
      employment: <Work />,
      criminal: <Gavel />,
      corporate: <Business />,
      banking: <AccountBalance />,
      taxation: <School />,
      property: <Home />,
    };
    return iconMap[iconName] || <Assignment />;
  };

