                </Typography>
              </Card>

              <Button
                variant="contained"
                fullWidth
                disabled={!title.trim() || !description.trim()}
                onClick={handleSubmit}
                sx={{
                  backgroundColor: (title.trim() && description.trim()) ? '#FFA500' : '#D3D3D3',
                  borderRadius: 2,
                  height: 50,
                }}
              >
                Submit
              </Button>
            </Box>
          )}

          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
            <Button
              variant="outlined"
              onClick={() => {
                if (activeStep > 0) {
                  setActiveStep(activeStep - 1);
                } else {
                  navigate('/dashboard');
                }
              }}
            >
              {activeStep === 0 ? 'Back to Dashboard' : 'Back'}
            </Button>
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
};

