import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Avatar,
  Chip,
  Grid,
  Divider,
  Skeleton,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import {
  Person,
  School,
  Work,
  LocationOn,
  Star,
  Business,
  Description,
} from '@mui/icons-material';
import { useParams, useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';
import { useAuth } from '../context/AuthContext';
import Breadcrumbs from '../components/Breadcrumbs';

interface LawyerDetails {
  _id: string;
  first_name: string;
  last_name: string;
  email: string;
  phone: string;
  picture?: string;
  lawyer_details: {
    designation: string;
    qualifications: string;
    station: string;
    years_of_experience: number;
    area_of_expertise: string;
    office_address: string;
    council_id: string;
    cnic_number: string;
  };
  matterCount: number;
  casesCount: number;
}

const LawyerProfile: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { user } = useAuth();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const [lawyer, setLawyer] = useState<LawyerDetails | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLawyerProfile = async () => {
      try {
        setLoading(true);
        // TODO: Replace with actual API call
        // const response = await api.get(`/users/profile/${id}`);
        // setLawyer(response.data);

        // Mock data for now
        await new Promise(resolve => setTimeout(resolve, 1000));
        setLawyer({
          _id: id || '1',
          first_name: 'Sarah',
          last_name: 'Ahmed',
          email: 'sarah.ahmed@lawfirm.com',
          phone: '+92-300-1234567',
          picture: '',
          lawyer_details: {
            designation: 'Senior Advocate',
            qualifications: 'LLB, LLM, Bar-at-Law',
            station: 'High Court',
            years_of_experience: 15,
            area_of_expertise: 'Criminal Law, Family Law, Corporate Law',
            office_address: '123 Legal Street, Blue Area, Islamabad',
