import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Chip,
  LinearProgress,
  Paper,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import {
  TrendingUp,
  People,
  Assignment,
  Chat,
  CalendarToday,
  Notifications,
} from '@mui/icons-material';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';

interface DashboardStats {
  totalCases: number;
  activeCases: number;
  totalClients: number;
  upcomingHearings: number;
  pendingTasks: number;
  recentMessages: number;
}

interface RecentCase {
  id: string;
  title: string;
  client: string;
  status: string;
  nextHearing: string;
  progress: number;
}

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState<DashboardStats>({
    totalCases: 0,
    activeCases: 0,
    totalClients: 0,
    upcomingHearings: 0,
    pendingTasks: 0,
    recentMessages: 0,
  });

  const [recentCases, setRecentCases] = useState<RecentCase[]>([]);
  const [openCaseDialog, setOpenCaseDialog] = useState(false);
  const [newCase, setNewCase] = useState({
    title: '',
    client: '',
    category: '',
    description: '',
  });

  useEffect(() => {
    // TODO: Fetch dashboard data from API
    setStats({
      totalCases: 24,
      activeCases: 18,
      totalClients: 12,
      upcomingHearings: 5,
      pendingTasks: 8,
      recentMessages: 3,
    });

    setRecentCases([
      {
        id: '1',
        title: 'Criminal Case vs. State',
        client: 'Ahmed Khan',
        status: 'Active',
        nextHearing: '2024-02-15',
        progress: 75,
      },
      {
        id: '2',
        title: 'Civil Suit - Property Dispute',
        client: 'Fatima Ali',
        status: 'Pending',
        nextHearing: '2024-02-20',
        progress: 45,
      },
      {
        id: '3',
        title: 'Family Law - Divorce',
        client: 'Muhammad Hassan',
        status: 'Active',
        nextHearing: '2024-02-18',
        progress: 60,
      },
    ]);
  }, []);

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'active':
        return Colors.success;
      case 'pending':
        return Colors.warning;
      case 'closed':
        return Colors.error;
      default:
        return Colors.gray500;
    }
  };

  const handleAddNewCase = () => {
    setOpenCaseDialog(true);
  };

  const handleCloseCaseDialog = () => {
    setOpenCaseDialog(false);
    setNewCase({ title: '', client: '', category: '', description: '' });
  };

  const handleSaveCase = () => {
    // TODO: Save case to API
    const caseToAdd: RecentCase = {
      id: Date.now().toString(),
      title: newCase.title,
      client: newCase.client,
      status: 'Active',
      nextHearing: new Date().toISOString().split('T')[0],
      progress: 0,
    };

    setRecentCases([caseToAdd, ...recentCases]);
    setStats(prev => ({
      ...prev,
      totalCases: prev.totalCases + 1,
      activeCases: prev.activeCases + 1,
    }));

    handleCloseCaseDialog();
  };

  const handleQuickAction = (action: string) => {
    switch (action) {
      case 'addCase':
        handleAddNewCase();
        break;
      case 'scheduleHearing':
        navigate('/calendar');
        break;
      case 'sendMessage':
        navigate('/chat');
        break;
      case 'uploadDocument':
        navigate('/documents');
        break;
      default:
        break;
    }
  };

  const StatCard: React.FC<{
    title: string;
    value: number;
    icon: React.ReactNode;
    color: string;
  }> = ({ title, value, icon, color }) => (
    <Card sx={{ height: '100%' }}>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Box>
            <Typography color="textSecondary" gutterBottom variant="body2">
              {title}
            </Typography>
            <Typography variant="h4" component="div" fontWeight="bold" color={color}>
              {value}
            </Typography>
          </Box>
          <Avatar sx={{ bgcolor: color, width: 56, height: 56 }}>
            {icon}
          </Avatar>
        </Box>
      </CardContent>
    </Card>
  );

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom fontWeight="bold">
        Welcome back, {user?.first_name}!
      </Typography>

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Total Cases"
            value={stats.totalCases}
            icon={<Assignment />}
            color={Colors.primary}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Active Cases"
            value={stats.activeCases}
            icon={<TrendingUp />}
            color={Colors.success}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Total Clients"
            value={stats.totalClients}
            icon={<People />}
            color={Colors.secondary}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Upcoming Hearings"
            value={stats.upcomingHearings}
            icon={<CalendarToday />}
            color={Colors.warning}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Pending Tasks"
            value={stats.pendingTasks}
            icon={<Notifications />}
            color={Colors.error}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Recent Messages"
            value={stats.recentMessages}
            icon={<Chat />}
            color={Colors.info}
          />
        </Grid>
      </Grid>

      <Grid container spacing={3}>
        <Grid item xs={12} lg={8}>
          <Card>
            <CardContent>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                <Typography variant="h6" fontWeight="bold">
                  Recent Cases
                </Typography>
                <Button variant="outlined" color="primary" onClick={() => navigate('/cases')}>
                  View All
                </Button>
              </Box>

              <List>
                {recentCases.map((caseItem, index) => (
                  <React.Fragment key={caseItem.id}>
                    <ListItem alignItems="flex-start">
                      <ListItemAvatar>
                        <Avatar sx={{ bgcolor: Colors.primary }}>
                          {index + 1}
                        </Avatar>
                      </ListItemAvatar>
                      <ListItemText
                        primary={
                          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <Typography variant="subtitle1" fontWeight="bold">
                              {caseItem.title}
                            </Typography>
                            <Chip
                              label={caseItem.status}
                              size="small"
                              sx={{
                                backgroundColor: getStatusColor(caseItem.status),
                                color: 'white',
                              }}
                            />
                          </Box>
                        }
                        secondary={
                          <Box>
                            <Typography variant="body2" color="textSecondary">
                              Client: {caseItem.client}
                            </Typography>
                            <Typography variant="body2" color="textSecondary">
                              Next Hearing: {caseItem.nextHearing}
                            </Typography>
                            <Box sx={{ mt: 1 }}>
                              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
                                <Typography variant="body2" color="textSecondary">
                                  Progress
                                </Typography>
                                <Typography variant="body2" color="textSecondary">
                                  {caseItem.progress}%
                                </Typography>
                              </Box>
                              <LinearProgress
                                variant="determinate"
                                value={caseItem.progress}
                                sx={{
                                  height: 6,
                                  borderRadius: 3,
                                  backgroundColor: Colors.gray200,
                                  '& .MuiLinearProgress-bar': {
                                    backgroundColor: Colors.primary,
                                  },
                                }}
                              />
                            </Box>
                          </Box>
                        }
                      />
                    </ListItem>
                    {index < recentCases.length - 1 && <Divider variant="inset" component="li" />}
                  </React.Fragment>
                ))}
              </List>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} lg={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" fontWeight="bold" gutterBottom>
                Quick Actions
              </Typography>

              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                <Button
                  variant="contained"
                  fullWidth
                  sx={{ backgroundColor: Colors.primary }}
                  onClick={() => handleQuickAction('addCase')}
                >
                  Add New Case
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  onClick={() => handleQuickAction('scheduleHearing')}
                >
                  Schedule Hearing
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  onClick={() => handleQuickAction('sendMessage')}
                >
                  Send Message
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  onClick={() => handleQuickAction('uploadDocument')}
                >
                  Upload Document
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Add New Case Dialog */}
      <Dialog open={openCaseDialog} onClose={handleCloseCaseDialog} maxWidth="md" fullWidth>
        <DialogTitle>Add New Case</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              fullWidth
              label="Case Title"
              value={newCase.title}
              onChange={(e) => setNewCase({ ...newCase, title: e.target.value })}
              required
            />
            <TextField
              fullWidth
              label="Client Name"
              value={newCase.client}
              onChange={(e) => setNewCase({ ...newCase, client: e.target.value })}
              required
            />
            <FormControl fullWidth>
              <InputLabel>Case Category</InputLabel>
              <Select
                value={newCase.category}
                label="Case Category"
                onChange={(e) => setNewCase({ ...newCase, category: e.target.value })}
              >
                <MenuItem value="criminal">Criminal</MenuItem>
                <MenuItem value="civil">Civil</MenuItem>
                <MenuItem value="family">Family</MenuItem>
                <MenuItem value="corporate">Corporate</MenuItem>
                <MenuItem value="property">Property</MenuItem>
              </Select>
            </FormControl>
            <TextField
              fullWidth
              label="Case Description"
              value={newCase.description}
              onChange={(e) => setNewCase({ ...newCase, description: e.target.value })}
              multiline
              rows={4}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseCaseDialog}>Cancel</Button>
          <Button 
            variant="contained" 
            onClick={handleSaveCase}
            disabled={!newCase.title || !newCase.client || !newCase.category}
            sx={{ backgroundColor: Colors.primary }}
          >
            Save Case
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Dashboard;
