import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Chip,
  IconButton,
  TextField,
  InputAdornment,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Grid,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Divider,
  Badge,
  LinearProgress,
} from '@mui/material';
import {
  Search,
  Add,
  Edit,
  Delete,
  Visibility,
  FilterList,
  Chat,
  Message,
  Assignment,
  Description,
  Gavel,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';

interface LegalMatter {
  id: string;
  title: string;
  category: string;
  subcategory: string;
  status: string;
  description: string;
  createdAt: string;
  progress: number;
  lawyer?: string;
  lawyerId?: string;
  comments: MatterComment[];
  urgency: 'high' | 'medium' | 'low';
  case_number?: string;
  opposing_party: string;
  court?: string;
}

interface MatterComment {
  id: string;
  author: string;
  authorType: 'lawyer' | 'client' | 'paralegal';
  message: string;
  timestamp: string;
  isRead: boolean;
}

const MyMatters: React.FC = () => {
  const navigate = useNavigate();
  const [matters, setMatters] = useState<LegalMatter[]>([]);
  const [filteredMatters, setFilteredMatters] = useState<LegalMatter[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [selectedMatter, setSelectedMatter] = useState<LegalMatter | null>(null);
  const [openDetailDialog, setOpenDetailDialog] = useState(false);
  const [openChatDialog, setOpenChatDialog] = useState(false);
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    // Mock data - replace with API call
    const mockMatters: LegalMatter[] = [
      {
        id: '1',
        title: 'Property Dispute with Neighbor',
        category: 'Civil Law',
        subcategory: 'Property Disputes',
        status: 'In Progress',
        description: 'Neighbor is encroaching on my property boundary. Need legal assistance to resolve this dispute.',
        createdAt: '2024-02-10',
        progress: 75,
        urgency: 'medium',
        opposing_party: 'Mr. Ali Hassan',
        court: 'District Court Islamabad',
        lawyer: 'Adv. Sarah Ahmed',
        lawyerId: 'lawyer1',
        comments: [
          {
            id: '1',
            author: 'Adv. Sarah Ahmed',
            authorType: 'lawyer',
            message: 'I have reviewed your case. We need to file a boundary dispute petition. Can you provide the property documents?',
            timestamp: '2024-02-12 10:30 AM',
            isRead: true,
          },
          {
            id: '2',
            author: 'You',
            authorType: 'client',
            message: 'I have all the property documents ready. When can we file the petition?',
            timestamp: '2024-02-12 11:15 AM',
            isRead: true,
          },
        ],
      },
      {
        id: '2',
        title: 'Employment Contract Dispute',
        category: 'Labor Law',
        subcategory: 'Employment Contracts',
        status: 'Pending',
        description: 'Employer is not paying overtime as per contract. Need legal advice on how to proceed.',
        createdAt: '2024-02-08',
        progress: 45,
        urgency: 'high',
        opposing_party: 'ABC Company Ltd.',
        lawyer: 'Adv. Muhammad Hassan',
        lawyerId: 'lawyer2',
        comments: [
          {
            id: '3',
            author: 'Adv. Muhammad Hassan',
            authorType: 'lawyer',
            message: 'This is a clear violation of labor laws. We should file a complaint with the labor court.',
            timestamp: '2024-02-09 02:30 PM',
            isRead: false,
          },
        ],
      },
      {
        id: '3',
        title: 'Divorce Proceedings',
        category: 'Family Law',
        subcategory: 'Divorce',
        status: 'Completed',
        description: 'Mutual consent divorce. All terms have been agreed upon.',
        createdAt: '2024-01-25',
        progress: 100,
        urgency: 'low',
        opposing_party: 'Spouse',
        court: 'Family Court Lahore',
        lawyer: 'Adv. Ayesha Khan',
        lawyerId: 'lawyer3',
        comments: [
          {
            id: '4',
            author: 'Adv. Ayesha Khan',
            authorType: 'lawyer',
            message: 'Divorce decree has been issued. The matter is now closed.',
            timestamp: '2024-01-30 09:45 AM',
            isRead: true,
          },
        ],
      },
    ];

    setMatters(mockMatters);
    setFilteredMatters(mockMatters);
  }, []);

  useEffect(() => {
    let filtered = matters;

    // Apply search filter
    if (searchTerm) {
      filtered = filtered.filter(matter =>
        matter.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        matter.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
        matter.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Apply status filter
    if (statusFilter !== 'all') {
      filtered = filtered.filter(matter => matter.status.toLowerCase() === statusFilter.toLowerCase());
    }

    setFilteredMatters(filtered);
  }, [searchTerm, statusFilter, matters]);

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'completed':
        return Colors.success;
      case 'in progress':
        return Colors.warning;
      case 'pending':
        return Colors.error;
      default:
        return Colors.gray500;
    }
  };

  const getUrgencyColor = (urgency: string) => {
    switch (urgency) {
      case 'high':
        return Colors.error;
      case 'medium':
        return Colors.warning;
      case 'low':
        return Colors.success;
      default:
        return Colors.gray500;
    }
  };

  const handleViewDetails = (matter: LegalMatter) => {
    setSelectedMatter(matter);
    setOpenDetailDialog(true);
  };

  const handleOpenChat = (matter: LegalMatter) => {
    setSelectedMatter(matter);
    setOpenChatDialog(true);
  };

  const handleSendComment = () => {
    if (!newComment.trim() || !selectedMatter) return;

    const comment: MatterComment = {
      id: Date.now().toString(),
      author: 'You',
      authorType: 'client',
      message: newComment,
      timestamp: new Date().toLocaleString(),
      isRead: false,
    };

    const updatedMatter = {
      ...selectedMatter,
      comments: [...selectedMatter.comments, comment],
    };

    setMatters(prev => prev.map(matter => matter.id === selectedMatter.id ? updatedMatter : matter));
    setSelectedMatter(updatedMatter);
    setNewComment('');
  };

  const getUnreadCount = (matter: LegalMatter) => {
    return matter.comments.filter(comment => !comment.isRead && comment.authorType !== 'client').length;
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1" fontWeight="bold">
          My Matters
        </Typography>
        <Button
          variant="contained"
          startIcon={<Add />}
          sx={{ backgroundColor: '#5491C9' }}
          onClick={() => navigate('/post-matter')}
        >
          Post New Matter
        </Button>
      </Box>

      {/* Filters */}
      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Grid container spacing={2} alignItems="center">
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                placeholder="Search matters..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Search />
                    </InputAdornment>
                  ),
                }}
              />
            </Grid>
            <Grid item xs={12} md={3}>
              <TextField
                select
                fullWidth
                label="Status"
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <FilterList />
                    </InputAdornment>
                  ),
                }}
              >
                <option value="all">All Status</option>
                <option value="pending">Pending</option>
                <option value="in progress">In Progress</option>
                <option value="completed">Completed</option>
              </TextField>
            </Grid>
            <Grid item xs={12} md={3}>
              <Typography variant="body2" color="textSecondary">
                {filteredMatters.length} of {matters.length} matters
              </Typography>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      {/* Matters Table */}
      <Card>
        <CardContent>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Matter</TableCell>
                  <TableCell>Category</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Urgency</TableCell>
                  <TableCell>Lawyer</TableCell>
                  <TableCell>Progress</TableCell>
                  <TableCell>Messages</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredMatters.map((matter) => (
                  <TableRow key={matter.id} hover>
                    <TableCell>
                      <Box>
                        <Typography variant="subtitle2" fontWeight="bold">
                          {matter.title}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          {matter.category} • {matter.subcategory}
                        </Typography>
                        <Typography variant="caption" color="textSecondary">
                          Created: {matter.createdAt}
                        </Typography>
                      </Box>
                    </TableCell>
                    <TableCell>
                      <Chip
                        label={matter.category}
                        size="small"
                        variant="outlined"
                        color="primary"
                      />
                    </TableCell>
                    <TableCell>
                      <Chip
                        label={matter.status}
                        size="small"
                        sx={{
                          backgroundColor: getStatusColor(matter.status),
                          color: 'white',
                        }}
                      />
                    </TableCell>
                    <TableCell>
                      <Chip
                        label={matter.urgency}
                        size="small"
                        sx={{
                          backgroundColor: getUrgencyColor(matter.urgency),
                          color: 'white',
                        }}
                      />
                    </TableCell>
                    <TableCell>
                      {matter.lawyer ? (
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Avatar sx={{ width: 24, height: 24, fontSize: '0.75rem' }}>
                            {matter.lawyer.charAt(0)}
                          </Avatar>
                          <Typography variant="body2">
                            {matter.lawyer}
                          </Typography>
                        </Box>
                      ) : (
                        <Typography variant="body2" color="textSecondary">
                          Unassigned
                        </Typography>
                      )}
                    </TableCell>
                    <TableCell>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <LinearProgress
                          variant="determinate"
                          value={matter.progress}
                          sx={{
                            width: 60,
                            height: 6,
                            borderRadius: 3,
                            backgroundColor: Colors.gray200,
                            '& .MuiLinearProgress-bar': {
                              backgroundColor: Colors.primary,
                            },
                          }}
                        />
                        <Typography variant="body2">
                          {matter.progress}%
                        </Typography>
                      </Box>
                    </TableCell>
                    <TableCell>
                      <Badge badgeContent={getUnreadCount(matter)} color="error">
                        <Typography variant="body2">
                          {matter.comments.length} messages
                        </Typography>
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <Box sx={{ display: 'flex', gap: 1 }}>
                        <IconButton
                          size="small"
                          onClick={() => handleViewDetails(matter)}
                          sx={{ color: Colors.primary }}
                        >
                          <Visibility />
                        </IconButton>
                        <IconButton
                          size="small"
                          onClick={() => handleOpenChat(matter)}
                          sx={{ color: Colors.primary }}
                        >
                          <Chat />
                        </IconButton>
                      </Box>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>
      </Card>

      {/* Matter Details Dialog */}
      <Dialog open={openDetailDialog} onClose={() => setOpenDetailDialog(false)} maxWidth="md" fullWidth>
        <DialogTitle>
          <Typography variant="h6" fontWeight="bold">
            Matter Details
          </Typography>
        </DialogTitle>
        <DialogContent>
          {selectedMatter && (
            <Box sx={{ pt: 2 }}>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <Typography variant="h6" gutterBottom>
                    {selectedMatter.title}
                  </Typography>
                  <Typography variant="body2" color="textSecondary" paragraph>
                    {selectedMatter.description}
                  </Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="textSecondary">
                    Category
                  </Typography>
                  <Typography variant="body1">
                    {selectedMatter.category} • {selectedMatter.subcategory}
                  </Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="textSecondary">
                    Status
                  </Typography>
                  <Chip
                    label={selectedMatter.status}
                    sx={{
                      backgroundColor: getStatusColor(selectedMatter.status),
                      color: 'white',
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="textSecondary">
                    Urgency
                  </Typography>
                  <Chip
                    label={selectedMatter.urgency}
                    sx={{
                      backgroundColor: getUrgencyColor(selectedMatter.urgency),
                      color: 'white',
                    }}
                  />
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="textSecondary">
                    Progress
                  </Typography>
                  <Typography variant="body1">
                    {selectedMatter.progress}%
                  </Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="textSecondary">
                    Opposing Party
                  </Typography>
                  <Typography variant="body1">
                    {selectedMatter.opposing_party}
                  </Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="textSecondary">
                    Court
                  </Typography>
                  <Typography variant="body1">
                    {selectedMatter.court || 'Not specified'}
                  </Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="textSecondary">
                    Created
                  </Typography>
                  <Typography variant="body1">
                    {selectedMatter.createdAt}
                  </Typography>
                </Grid>

                <Grid item xs={12} sm={6}>
                  <Typography variant="subtitle2" color="textSecondary">
                    Assigned Lawyer
                  </Typography>
                  <Typography variant="body1">
                    {selectedMatter.lawyer || 'Unassigned'}
                  </Typography>
                </Grid>
              </Grid>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDetailDialog(false)}>Close</Button>
          {selectedMatter && (
            <Button
              variant="contained"
              onClick={() => {
                setOpenDetailDialog(false);
                handleOpenChat(selectedMatter);
              }}
              sx={{ backgroundColor: Colors.primary }}
            >
              Open Chat
            </Button>
          )}
        </DialogActions>
      </Dialog>

      {/* Chat Dialog */}
      <Dialog open={openChatDialog} onClose={() => setOpenChatDialog(false)} maxWidth="md" fullWidth>
        <DialogTitle>
          <Typography variant="h6" fontWeight="bold">
            Chat - {selectedMatter?.title}
          </Typography>
        </DialogTitle>
        <DialogContent>
          {selectedMatter && (
            <Box sx={{ pt: 2 }}>
              <Box sx={{ height: 400, overflowY: 'auto', mb: 2 }}>
                <List>
                  {selectedMatter.comments.map((comment, index) => (
                    <React.Fragment key={comment.id}>
                      <ListItem alignItems="flex-start">
                        <ListItemAvatar>
                          <Avatar sx={{ 
                            bgcolor: comment.authorType === 'lawyer' ? Colors.primary : Colors.secondary,
                            width: 40,
                            height: 40,
                          }}>
                            {comment.author.charAt(0)}
                          </Avatar>
                        </ListItemAvatar>
                        <ListItemText
                          primary={
                            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                              <Typography variant="subtitle2" fontWeight="bold">
                                {comment.author}
                              </Typography>
                              <Typography variant="caption" color="textSecondary">
                                {comment.timestamp}
                              </Typography>
                            </Box>
                          }
                          secondary={
                            <Typography variant="body2" sx={{ mt: 1 }}>
                              {comment.message}
                            </Typography>
                          }
                        />
                      </ListItem>
                      {index < selectedMatter.comments.length - 1 && <Divider variant="inset" component="li" />}
                    </React.Fragment>
                  ))}
                </List>
              </Box>

              <Box sx={{ display: 'flex', gap: 1 }}>
                <TextField
                  fullWidth
                  placeholder="Type your message..."
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  multiline
                  rows={2}
                />
                <Button
                  variant="contained"
                  onClick={handleSendComment}
                  disabled={!newComment.trim()}
                  sx={{ backgroundColor: Colors.primary }}
                >
                  Send
                </Button>
              </Box>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenChatDialog(false)}>Close</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default MyMatters;
