import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stepper,
  Step,
  StepLabel,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Grid,
  Chip,
} from '@mui/material';
import {
  Gavel,
  Description,
  Assignment,
  Business,
  Home,
  Work,
  AccountBalance,
  School,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';

interface MatterCategory {
  _id: string;
  name: string;
  icon: React.ReactNode;
  description: string;
  subcategories: MatterSubcategory[];
}

interface MatterSubcategory {
  _id: string;
  name: string;
  description: string;
}

const PostMatter: React.FC = () => {
  const navigate = useNavigate();
  const [activeStep, setActiveStep] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState<MatterCategory | null>(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState<MatterSubcategory | null>(null);
  const [newMatter, setNewMatter] = useState({
    title: '',
    description: '',
    case_number: '',
    opposing_party: '',
    court: '',
    urgency: 'medium',
  });

  // Legal matter categories matching Android app
  const matterCategories: MatterCategory[] = [
    {
      _id: '1',
      name: 'Criminal Law',
      icon: <Gavel />,
      description: 'Criminal cases and proceedings',
      subcategories: [
        { _id: '1-1', name: 'Murder', description: 'Homicide cases' },
        { _id: '1-2', name: 'Theft', description: 'Property theft cases' },
        { _id: '1-3', name: 'Assault', description: 'Physical assault cases' },
        { _id: '1-4', name: 'Drug Offenses', description: 'Narcotics cases' },
        { _id: '1-5', name: 'Fraud', description: 'Financial fraud cases' },
        { _id: '1-6', name: 'Cyber Crime', description: 'Digital crime cases' },
      ]
    },
    {
      _id: '2',
      name: 'Civil Law',
      icon: <Description />,
      description: 'Civil disputes and litigation',
      subcategories: [
        { _id: '2-1', name: 'Contract Disputes', description: 'Contractual disagreements' },
        { _id: '2-2', name: 'Property Disputes', description: 'Real estate conflicts' },
        { _id: '2-3', name: 'Tort Claims', description: 'Personal injury cases' },
        { _id: '2-4', name: 'Landlord-Tenant', description: 'Rental disputes' },
        { _id: '2-5', name: 'Personal Injury', description: 'Accident claims' },
        { _id: '2-6', name: 'Medical Malpractice', description: 'Healthcare negligence' },
      ]
    },
    {
      _id: '3',
      name: 'Family Law',
      icon: <Home />,
      description: 'Family and domestic matters',
      subcategories: [
        { _id: '3-1', name: 'Divorce', description: 'Marriage dissolution' },
        { _id: '3-2', name: 'Child Custody', description: 'Child guardianship' },
        { _id: '3-3', name: 'Child Support', description: 'Child maintenance' },
        { _id: '3-4', name: 'Adoption', description: 'Child adoption process' },
        { _id: '3-5', name: 'Marriage Registration', description: 'Marital documentation' },
        { _id: '3-6', name: 'Domestic Violence', description: 'Family abuse cases' },
      ]
    },
    {
      _id: '4',
      name: 'Corporate Law',
      icon: <Business />,
      description: 'Business and corporate matters',
      subcategories: [
        { _id: '4-1', name: 'Company Registration', description: 'Business incorporation' },
        { _id: '4-2', name: 'Corporate Governance', description: 'Board management' },
        { _id: '4-3', name: 'Mergers & Acquisitions', description: 'Business combinations' },
        { _id: '4-4', name: 'Intellectual Property', description: 'IP rights protection' },
        { _id: '4-5', name: 'Employment Law', description: 'Workplace disputes' },
        { _id: '4-6', name: 'Tax Law', description: 'Taxation matters' },
      ]
    },
    {
      _id: '5',
      name: 'Property Law',
      icon: <Home />,
      description: 'Real estate and property matters',
      subcategories: [
        { _id: '5-1', name: 'Real Estate', description: 'Property transactions' },
        { _id: '5-2', name: 'Property Registration', description: 'Title documentation' },
        { _id: '5-3', name: 'Land Disputes', description: 'Land ownership conflicts' },
        { _id: '5-4', name: 'Property Tax', description: 'Real estate taxation' },
        { _id: '5-5', name: 'Rental Agreements', description: 'Lease contracts' },
        { _id: '5-6', name: 'Property Development', description: 'Construction projects' },
      ]
    },
    {
      _id: '6',
      name: 'Constitutional Law',
      icon: <AccountBalance />,
      description: 'Constitutional and administrative matters',
      subcategories: [
        { _id: '6-1', name: 'Fundamental Rights', description: 'Basic rights protection' },
        { _id: '6-2', name: 'Public Interest Litigation', description: 'PIL cases' },
        { _id: '6-3', name: 'Administrative Law', description: 'Government actions' },
        { _id: '6-4', name: 'Election Law', description: 'Electoral disputes' },
        { _id: '6-5', name: 'Human Rights', description: 'Human rights violations' },
        { _id: '6-6', name: 'Judicial Review', description: 'Court oversight' },
      ]
    },
    {
      _id: '7',
      name: 'Banking & Finance',
      icon: <AccountBalance />,
      description: 'Financial and banking matters',
      subcategories: [
        { _id: '7-1', name: 'Banking Regulations', description: 'Bank compliance' },
        { _id: '7-2', name: 'Securities Law', description: 'Investment regulations' },
        { _id: '7-3', name: 'Insurance Law', description: 'Insurance disputes' },
        { _id: '7-4', name: 'Debt Recovery', description: 'Loan recovery' },
        { _id: '7-5', name: 'Financial Fraud', description: 'Financial crimes' },
        { _id: '7-6', name: 'Investment Law', description: 'Investment disputes' },
      ]
    },
    {
      _id: '8',
      name: 'Labor Law',
      icon: <Work />,
      description: 'Employment and labor matters',
      subcategories: [
        { _id: '8-1', name: 'Employment Contracts', description: 'Work agreements' },
        { _id: '8-2', name: 'Workplace Safety', description: 'Safety violations' },
        { _id: '8-3', name: 'Industrial Disputes', description: 'Labor conflicts' },
        { _id: '8-4', name: 'Workers Compensation', description: 'Injury compensation' },
        { _id: '8-5', name: 'Trade Unions', description: 'Union matters' },
        { _id: '8-6', name: 'Minimum Wage', description: 'Wage disputes' },
      ]
    }
  ];

  const steps = ['Select Category', 'Select Subcategory', 'Matter Details'];

  const handleCategorySelect = (category: MatterCategory) => {
    setSelectedCategory(category);
    setActiveStep(1);
  };

  const handleSubcategorySelect = (subcategory: MatterSubcategory) => {
    setSelectedSubcategory(subcategory);
    setActiveStep(2);
  };

  const handleSaveMatter = () => {
    // TODO: Save matter to API
    console.log('Saving matter:', {
      ...newMatter,
      category: selectedCategory?.name,
      subcategory: selectedSubcategory?.name,
    });

    // Navigate back to dashboard or matters list
    navigate('/my-matters');
  };

  const getUrgencyColor = (urgency: string) => {
    switch (urgency) {
      case 'high':
        return Colors.error;
      case 'medium':
        return Colors.warning;
      case 'low':
        return Colors.success;
      default:
        return Colors.gray500;
    }
  };

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom fontWeight="bold">
        Post Your Matter
      </Typography>

      <Typography variant="body1" color="textSecondary" sx={{ mb: 4 }}>
        Post your legal matter to get assistance from qualified lawyers
      </Typography>

      <Card>
        <CardContent>
          <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          {activeStep === 0 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Select Legal Category
              </Typography>
              <Typography variant="body2" color="textSecondary" sx={{ mb: 3 }}>
                Choose the category that best describes your legal matter
              </Typography>

              <Grid container spacing={3}>
                {matterCategories.map((category) => (
                  <Grid item xs={12} sm={6} md={4} key={category._id}>
                    <Card
                      sx={{
                        cursor: 'pointer',
                        border: selectedCategory?._id === category._id ? `2px solid ${Colors.primary}` : '1px solid #e0e0e0',
                        '&:hover': { borderColor: Colors.primary },
                      }}
                      onClick={() => handleCategorySelect(category)}
                    >
                      <CardContent sx={{ textAlign: 'center', py: 3 }}>
                        <Box sx={{ color: Colors.primary, mb: 2 }}>
                          {category.icon}
                        </Box>
                        <Typography variant="h6" fontWeight="bold" gutterBottom>
                          {category.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          {category.description}
                        </Typography>
                        <Chip
                          label={`${category.subcategories.length} subcategories`}
                          size="small"
                          sx={{ mt: 1 }}
                        />
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {activeStep === 1 && selectedCategory && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Select Subcategory - {selectedCategory.name}
              </Typography>
              <Typography variant="body2" color="textSecondary" sx={{ mb: 3 }}>
                Choose the specific type of legal matter
              </Typography>

              <Grid container spacing={2}>
                {selectedCategory.subcategories.map((subcategory) => (
                  <Grid item xs={12} sm={6} key={subcategory._id}>
                    <Card
                      sx={{
                        cursor: 'pointer',
                        border: selectedSubcategory?._id === subcategory._id ? `2px solid ${Colors.primary}` : '1px solid #e0e0e0',
                        '&:hover': { borderColor: Colors.primary },
                      }}
                      onClick={() => handleSubcategorySelect(subcategory)}
                    >
                      <CardContent sx={{ py: 2 }}>
                        <Typography variant="subtitle1" fontWeight="medium" gutterBottom>
                          {subcategory.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          {subcategory.description}
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {activeStep === 2 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Matter Details
              </Typography>
              <Typography variant="body2" color="textSecondary" sx={{ mb: 3 }}>
                Provide detailed information about your legal matter
              </Typography>

              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <Typography variant="body2" color="textSecondary" gutterBottom>
                    Selected: {selectedCategory?.name} â€¢ {selectedSubcategory?.name}
                  </Typography>
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Matter Title"
                    value={newMatter.title}
                    onChange={(e) => setNewMatter({ ...newMatter, title: e.target.value })}
                    required
                  />
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Case Number (if any)"
                    value={newMatter.case_number}
                    onChange={(e) => setNewMatter({ ...newMatter, case_number: e.target.value })}
                  />
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Opposing Party"
                    value={newMatter.opposing_party}
                    onChange={(e) => setNewMatter({ ...newMatter, opposing_party: e.target.value })}
                    required
                  />
                </Grid>

                <Grid item xs={12} md={6}>
                  <TextField
                    fullWidth
                    label="Court (if applicable)"
                    value={newMatter.court}
                    onChange={(e) => setNewMatter({ ...newMatter, court: e.target.value })}
                  />
                </Grid>

                <Grid item xs={12} md={6}>
                  <FormControl fullWidth>
                    <InputLabel>Urgency Level</InputLabel>
                    <Select
                      value={newMatter.urgency}
                      label="Urgency Level"
                      onChange={(e) => setNewMatter({ ...newMatter, urgency: e.target.value })}
                    >
                      <MenuItem value="low">Low</MenuItem>
                      <MenuItem value="medium">Medium</MenuItem>
                      <MenuItem value="high">High</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>

                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Detailed Description"
                    value={newMatter.description}
                    onChange={(e) => setNewMatter({ ...newMatter, description: e.target.value })}
                    multiline
                    rows={6}
                    required
                    placeholder="Describe your legal matter in detail, including relevant facts, timeline, and what you're seeking..."
                  />
                </Grid>
              </Grid>
            </Box>
          )}

          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
            <Button
              variant="outlined"
              onClick={() => {
                if (activeStep > 0) {
                  setActiveStep(activeStep - 1);
                } else {
                  navigate('/dashboard');
                }
              }}
            >
              {activeStep === 0 ? 'Back to Dashboard' : 'Back'}
            </Button>

            {activeStep < 2 && selectedCategory && (
              <Button
                variant="contained"
                onClick={() => setActiveStep(activeStep + 1)}
                sx={{ backgroundColor: Colors.primary }}
              >
                Next
              </Button>
            )}

            {activeStep === 2 && (
              <Button
                variant="contained"
                onClick={handleSaveMatter}
                disabled={!newMatter.title || !newMatter.description || !newMatter.opposing_party}
                sx={{ backgroundColor: Colors.primary }}
              >
                Post Matter
              </Button>
            )}
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
};

export default PostMatter;
