import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  Grid,
  useTheme,
  useMediaQuery,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Chip,
  LinearProgress,
  Snackbar,
  Alert,
  Fade,
  Zoom,
  Backdrop,
  CircularProgress,
} from '@mui/material';
import {
  Gavel,
  Description,
  Assignment,
  Business,
  Home,
  Work,
  AccountBalance,
  School,
  Save,
  Close,
  CheckCircle,
  Warning,
  Info,
  ArrowBack,
  ArrowForward,
  KeyboardArrowRight,
  KeyboardArrowLeft,
  Balance,
  DocumentScanner,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';
import Breadcrumbs from '../components/Breadcrumbs';

interface RequestType {
  id: string;
  label: string;
  description: string;
  icon: string;
}

const AddRequest1: React.FC = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const isSmallMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const [selectedOption, setSelectedOption] = useState<string | null>(null);
  const [showExitDialog, setShowExitDialog] = useState(false);
  const [snackbar, setSnackbar] = useState<{ open: boolean; message: string; severity: 'success' | 'error' | 'info' }>({
    open: false,
    message: '',
    severity: 'info'
  });

  // Mock data matching Android app structure exactly
  const options: RequestType[] = [
    {
      id: 'judicial',
      label: 'Judicial',
      description: 'e.g. Certified Copies, Court Case Follow-ups, Surety/Bail Bonds etc.',
      icon: 'gavel',
    },
    {
      id: 'non_judicial',
      label: 'Non-Judicial',
      description: 'e.g. Birth Certificate, Death Certificate, Power of Attorney etc.',
      icon: 'document',
    },
  ];

  // Auto-save draft functionality
  const saveDraft = () => {
    const draft = {
      selectedType: selectedOption,
      timestamp: new Date().toISOString(),
    };
    localStorage.setItem('request-draft-step1', JSON.stringify(draft));
    setSnackbar({
      open: true,
      message: 'Draft saved automatically',
      severity: 'success'
    });
  };

  // Load draft on component mount
  useEffect(() => {
    const savedDraft = localStorage.getItem('request-draft-step1');
    if (savedDraft) {
      try {
        const draft = JSON.parse(savedDraft);
        setSelectedOption(draft.selectedType || null);
      } catch (error) {
        console.error('Error loading draft:', error);
      }
    }
  }, []);

  // Auto-save when form data changes
  useEffect(() => {
    const timeoutId = setTimeout(saveDraft, 2000);
    return () => clearTimeout(timeoutId);
  }, [selectedOption]);

  // Enhanced keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.ctrlKey || e.metaKey) {
        switch (e.key) {
          case 's':
            e.preventDefault();
            saveDraft();
            break;
          case 'Enter':
            e.preventDefault();
            if (selectedOption) {
              handleNext();
            }
            break;
        }
      } else if (e.key === 'Escape') {
        if (selectedOption) {
          setShowExitDialog(true);
        } else {
          navigate('/dashboard');
        }
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [selectedOption]);

  const handleNext = () => {
    if (selectedOption) {
      navigate('/add-request-2', { state: { type: selectedOption } });
    }
  };

  const handleBack = () => {
    if (selectedOption) {
      setShowExitDialog(true);
    } else {
      navigate('/dashboard');
    }
  };

  const getTypeIcon = (iconName: string) => {
    const iconMap: { [key: string]: React.ReactNode } = {
      gavel: <Gavel />,
      document: <DocumentScanner />,
    };
    return iconMap[iconName] || <Assignment />;
  };

  // Get breadcrumb items
  const getBreadcrumbItems = () => {
    return [
      { label: 'Post Your Request', path: '/dashboard' },
      { label: 'Select Type', current: true }
    ];
  };

  return (
    <Box sx={{ maxWidth: '100%', mx: 'auto', px: isMobile ? 1 : 3 }}>
      {/* Breadcrumbs */}
      <Breadcrumbs items={getBreadcrumbItems()} />

      {/* Progress indicator */}
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3, flexWrap: 'wrap', gap: 2 }}>
        <Typography variant={isMobile ? "body1" : "h6"} sx={{ mr: 2, fontWeight: 'bold' }}>
          Step 1 of 3
        </Typography>
        <LinearProgress 
          value={33.33} 
          sx={{ 
            flex: 1, 
            height: isMobile ? 6 : 8, 
            borderRadius: 4,
            minWidth: isMobile ? 150 : 200
          }}
        />
        <Chip 
          label="33%"
          color="primary"
          size={isMobile ? "small" : "medium"}
        />
      </Box>

      <Typography 
        variant={isMobile ? "h5" : "h4"} 
        component="h1" 
        gutterBottom 
        fontWeight="bold"
        sx={{ mb: 3 }}
      >
        Add Request
      </Typography>

      <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
        <CardContent sx={{ p: isMobile ? 2 : 3 }}>
          <Typography variant={isMobile ? "h6" : "h5"} gutterBottom sx={{ mb: 3 }}>
            Select Type
          </Typography>

          <Grid container spacing={isMobile ? 2 : 3}>
            {options.map((option) => (
              <Grid item xs={12} sm={6} key={option.id}>
                <Fade in={true} timeout={500}>
                  <Card
                    sx={{
                      cursor: 'pointer',
                      border: selectedOption === option.id ? `2px solid #FFA500` : '1px solid #D1D3D4',
                      borderRadius: 3,
                      backgroundColor: selectedOption === option.id ? '#FFA500' : 'white',
                      color: selectedOption === option.id ? 'white' : 'inherit',
                      '&:hover': { 
                        borderColor: '#FFA500',
                        transform: 'translateY(-4px)',
                        transition: 'all 0.3s ease-in-out',
                        boxShadow: 4
                      },
                      height: isMobile ? 200 : 250,
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'center',
                      justifyContent: 'center',
                      transition: 'all 0.3s ease-in-out',
                      p: isMobile ? 2 : 3,
                    }}
                    onClick={() => setSelectedOption(option.id)}
                  >
                    <Box sx={{ 
                      color: selectedOption === option.id ? 'white' : Colors.primary, 
                      mb: 2,
                      fontSize: isMobile ? '3rem' : '4rem'
                    }}>
                      {getTypeIcon(option.icon)}
                    </Box>
                    <Typography 
                      variant={isMobile ? "h6" : "h5"} 
                      textAlign="center" 
                      fontWeight="bold"
                      sx={{ mb: 2 }}
                    >
                      {option.label}
                    </Typography>
                    <Typography 
                      variant={isMobile ? "body2" : "body1"} 
                      textAlign="center"
                      sx={{ 
                        color: selectedOption === option.id ? 'rgba(255,255,255,0.9)' : 'text.secondary',
                        lineHeight: 1.5
                      }}
                    >
                      {option.description}
                    </Typography>
                  </Card>
                </Fade>
              </Grid>
            ))}
          </Grid>

          <Button
            variant="contained"
            fullWidth
            disabled={!selectedOption}
            onClick={handleNext}
            sx={{
              backgroundColor: selectedOption ? '#FFA500' : '#D3D3D3',
              mt: 4,
              borderRadius: 2,
              height: 50,
              '&:hover': {
                backgroundColor: selectedOption ? '#E69500' : '#D3D3D3',
              }
            }}
            endIcon={<KeyboardArrowRight />}
          >
            Next
          </Button>

          {/* Navigation buttons */}
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            mt: 4,
            flexDirection: isMobile ? 'column' : 'row',
            gap: isMobile ? 2 : 0
          }}>
            <Button
              variant="outlined"
              onClick={handleBack}
              startIcon={<ArrowBack />}
              sx={{ 
                minWidth: isMobile ? '100%' : 'auto',
                order: isMobile ? 2 : 1
              }}
            >
              Back to Dashboard
            </Button>

            {/* Keyboard shortcuts hint */}
            <Typography 
              variant="caption" 
              color="textSecondary" 
              sx={{ 
                alignSelf: 'center',
                order: isMobile ? 1 : 2,
                textAlign: isMobile ? 'center' : 'left'
              }}
            >
              💡 Tip: Press Ctrl+S to save draft, Ctrl+Enter to proceed
            </Typography>
          </Box>
        </CardContent>
      </Card>

      {/* Exit Confirmation Dialog */}
      <Dialog
        open={showExitDialog}
        onClose={() => setShowExitDialog(false)}
        maxWidth="sm"
        fullWidth
        TransitionComponent={Zoom}
      >
        <DialogTitle sx={{ pb: 1 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <Typography variant="h6" fontWeight="bold">
              Unsaved Changes
            </Typography>
            <IconButton onClick={() => setShowExitDialog(false)}>
              <Close />
            </IconButton>
          </Box>
        </DialogTitle>
        <DialogContent>
          <Typography>
            You have unsaved changes. Are you sure you want to leave? Your draft will be saved automatically.
          </Typography>
        </DialogContent>
        <DialogActions sx={{ p: 2, pt: 0 }}>
          <Button onClick={() => setShowExitDialog(false)}>
            Continue Editing
          </Button>
          <Button 
            onClick={() => {
              setShowExitDialog(false);
              navigate('/dashboard');
            }}
            variant="contained"
            color="error"
          >
            Leave Anyway
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          onClose={() => setSnackbar({ ...snackbar, open: false })} 
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

