import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  TextField,
  Stepper,
  Step,
  StepLabel,
  Grid,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  Alert,
  LinearProgress,
} from '@mui/material';
import {
  Gavel,
  Description,
  Assignment,
  Business,
  Home,
  Work,
  AccountBalance,
  School,
  Save,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';
import Breadcrumbs from '../components/Breadcrumbs';

interface MatterCategory {
  _id: string;
  name: string;
  icon: string;
}

interface MatterSubcategory {
  _id: string;
  name: string;
  icon: string;
  parent: string;
}

const PostMatter: React.FC = () => {
  const navigate = useNavigate();
  const [activeStep, setActiveStep] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState<MatterCategory | null>(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState<MatterSubcategory | null>(null);
  const [categories, setCategories] = useState<MatterCategory[]>([]);
  const [subcategories, setSubcategories] = useState<MatterSubcategory[]>([]);
  const [loading, setLoading] = useState(true);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [showDraftSaved, setShowDraftSaved] = useState(false);

  // Mock data matching Android app structure exactly
  const mockCategories: MatterCategory[] = [
    { _id: '1', name: 'Family Law', icon: 'family' },
    { _id: '2', name: 'Civil Law', icon: 'civil' },
    { _id: '3', name: 'Employment Law', icon: 'employment' },
    { _id: '4', name: 'Criminal Law', icon: 'criminal' },
    { _id: '5', name: 'Corporate Law', icon: 'corporate' },
    { _id: '6', name: 'Banking Law', icon: 'banking' },
    { _id: '7', name: 'Taxation Law', icon: 'taxation' },
    { _id: '8', name: 'Property Law', icon: 'property' },
  ];

  const mockSubcategories: { [key: string]: MatterSubcategory[] } = {
    '1': [ // Family Law
      { _id: '1-1', name: 'Divorce', icon: 'divorce', parent: '1' },
      { _id: '1-2', name: 'Child Custody', icon: 'custody', parent: '1' },
      { _id: '1-3', name: 'Child Support', icon: 'support', parent: '1' },
      { _id: '1-4', name: 'Adoption', icon: 'adoption', parent: '1' },
      { _id: '1-5', name: 'Marriage Registration', icon: 'marriage', parent: '1' },
      { _id: '1-6', name: 'Domestic Violence', icon: 'violence', parent: '1' },
    ],
    '2': [ // Civil Law
      { _id: '2-1', name: 'Contract Disputes', icon: 'contract', parent: '2' },
      { _id: '2-2', name: 'Property Disputes', icon: 'property', parent: '2' },
      { _id: '2-3', name: 'Tort Claims', icon: 'tort', parent: '2' },
      { _id: '2-4', name: 'Landlord-Tenant', icon: 'rental', parent: '2' },
      { _id: '2-5', name: 'Personal Injury', icon: 'injury', parent: '2' },
      { _id: '2-6', name: 'Medical Malpractice', icon: 'malpractice', parent: '2' },
    ],
    '3': [ // Employment Law
      { _id: '3-1', name: 'Employment Contracts', icon: 'contract', parent: '3' },
      { _id: '3-2', name: 'Workplace Safety', icon: 'safety', parent: '3' },
      { _id: '3-3', name: 'Industrial Disputes', icon: 'disputes', parent: '3' },
      { _id: '3-4', name: 'Workers Compensation', icon: 'compensation', parent: '3' },
      { _id: '3-5', name: 'Trade Unions', icon: 'unions', parent: '3' },
      { _id: '3-6', name: 'Minimum Wage', icon: 'wage', parent: '3' },
    ],
    '4': [ // Criminal Law
      { _id: '4-1', name: 'Murder', icon: 'murder', parent: '4' },
      { _id: '4-2', name: 'Theft', icon: 'theft', parent: '4' },
      { _id: '4-3', name: 'Assault', icon: 'assault', parent: '4' },
      { _id: '4-4', name: 'Drug Offenses', icon: 'drugs', parent: '4' },
      { _id: '4-5', name: 'Fraud', icon: 'fraud', parent: '4' },
      { _id: '4-6', name: 'Cyber Crime', icon: 'cyber', parent: '4' },
    ],
    '5': [ // Corporate Law
      { _id: '5-1', name: 'Company Registration', icon: 'registration', parent: '5' },
      { _id: '5-2', name: 'Corporate Governance', icon: 'governance', parent: '5' },
      { _id: '5-3', name: 'Mergers & Acquisitions', icon: 'mergers', parent: '5' },
      { _id: '5-4', name: 'Intellectual Property', icon: 'ip', parent: '5' },
      { _id: '5-5', name: 'Employment Law', icon: 'employment', parent: '5' },
      { _id: '5-6', name: 'Tax Law', icon: 'tax', parent: '5' },
    ],
    '6': [ // Banking Law
      { _id: '6-1', name: 'Banking Regulations', icon: 'regulations', parent: '6' },
      { _id: '6-2', name: 'Securities Law', icon: 'securities', parent: '6' },
      { _id: '6-3', name: 'Insurance Law', icon: 'insurance', parent: '6' },
      { _id: '6-4', name: 'Debt Recovery', icon: 'debt', parent: '6' },
      { _id: '6-5', name: 'Financial Fraud', icon: 'fraud', parent: '6' },
      { _id: '6-6', name: 'Investment Law', icon: 'investment', parent: '6' },
    ],
    '7': [ // Taxation Law
      { _id: '7-1', name: 'Income Tax', icon: 'income', parent: '7' },
      { _id: '7-2', name: 'Corporate Tax', icon: 'corporate', parent: '7' },
      { _id: '7-3', name: 'Sales Tax', icon: 'sales', parent: '7' },
      { _id: '7-4', name: 'Property Tax', icon: 'property', parent: '7' },
      { _id: '7-5', name: 'Tax Disputes', icon: 'disputes', parent: '7' },
      { _id: '7-6', name: 'Tax Planning', icon: 'planning', parent: '7' },
    ],
    '8': [ // Property Law
      { _id: '8-1', name: 'Real Estate', icon: 'realestate', parent: '8' },
      { _id: '8-2', name: 'Property Registration', icon: 'registration', parent: '8' },
      { _id: '8-3', name: 'Land Disputes', icon: 'land', parent: '8' },
      { _id: '8-4', name: 'Property Tax', icon: 'tax', parent: '8' },
      { _id: '8-5', name: 'Rental Agreements', icon: 'rental', parent: '8' },
      { _id: '8-6', name: 'Property Development', icon: 'development', parent: '8' },
    ],
  };

  // Auto-save draft functionality
  const saveDraft = () => {
    const draft = {
      title,
      description,
      selectedCategory: selectedCategory?._id,
      selectedSubcategory: selectedSubcategory?._id,
      activeStep,
      timestamp: new Date().toISOString(),
    };
    localStorage.setItem('matter-draft', JSON.stringify(draft));
    setShowDraftSaved(true);
    setTimeout(() => setShowDraftSaved(false), 2000);
  };

  // Load draft on component mount
  useEffect(() => {
    const savedDraft = localStorage.getItem('matter-draft');
    if (savedDraft) {
      try {
        const draft = JSON.parse(savedDraft);
        setTitle(draft.title || '');
        setDescription(draft.description || '');
        setActiveStep(draft.activeStep || 0);

        // Restore category and subcategory selections
        if (draft.selectedCategory) {
          const category = mockCategories.find(c => c._id === draft.selectedCategory);
          if (category) {
            setSelectedCategory(category);
            setSubcategories(mockSubcategories[category._id] || []);
          }
        }

        if (draft.selectedSubcategory) {
          const subcategory = subcategories.find(s => s._id === draft.selectedSubcategory);
          if (subcategory) {
            setSelectedSubcategory(subcategory);
          }
        }
      } catch (error) {
        console.error('Error loading draft:', error);
      }
    }
  }, []);

  // Auto-save when form data changes
  useEffect(() => {
    const timeoutId = setTimeout(saveDraft, 2000); // Save after 2 seconds of inactivity
    return () => clearTimeout(timeoutId);
  }, [title, description, selectedCategory, selectedSubcategory, activeStep]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.ctrlKey || e.metaKey) {
        switch (e.key) {
          case 's':
            e.preventDefault();
            saveDraft();
            break;
          case 'Enter':
            e.preventDefault();
            if (activeStep < 2 && selectedCategory) {
              setActiveStep(activeStep + 1);
            } else if (activeStep === 2 && title.trim() && description.trim()) {
              handleSubmit();
            }
            break;
        }
      } else if (e.key === 'Escape') {
        navigate('/dashboard');
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [activeStep, selectedCategory, title, description]);

  useEffect(() => {
    // Simulate API call to fetch categories
    setTimeout(() => {
      setCategories(mockCategories);
      setLoading(false);
    }, 1000);
  }, []);

  const handleCategorySelect = (category: MatterCategory) => {
    setSelectedCategory(category);
    setSubcategories(mockSubcategories[category._id] || []);
    setActiveStep(1);
  };

  const handleSubcategorySelect = (subcategory: MatterSubcategory) => {
    setSelectedSubcategory(subcategory);
    setActiveStep(2);
  };

  const handleSubmit = () => {
    // Submit to API - exact same payload as Android app
    console.log('Submitting matter:', {
      category_id: selectedCategory?._id,
      subcategory_id: selectedSubcategory?._id,
      title,
      description,
    });

    // Clear draft after successful submission
    localStorage.removeItem('matter-draft');

    // Navigate back to dashboard
    navigate('/my-matters');
  };

  const getCategoryIcon = (iconName: string) => {
    const iconMap: { [key: string]: React.ReactNode } = {
      family: <Home />,
      civil: <Description />,
      employment: <Work />,
      criminal: <Gavel />,
      corporate: <Business />,
      banking: <AccountBalance />,
      taxation: <School />,
      property: <Home />,
    };
    return iconMap[iconName] || <Assignment />;
  };

  const steps = ['Select Type', 'Select Services', 'Describe Your Matter'];

  // Get breadcrumb items based on current step
  const getBreadcrumbItems = () => {
    const items: Array<{ label: string; path?: string; current?: boolean }> = [
      { label: 'Post Your Matter', path: '/post-matter' }
    ];

    if (activeStep >= 1) {
      items.push({ label: steps[0] });
    }

    if (activeStep >= 2) {
      items.push({ label: steps[1] });
    }

    if (activeStep === 2) {
      items.push({ label: steps[2], current: true });
    }

    return items;
  };

  return (
    <Box>
      {/* Breadcrumbs */}
      <Breadcrumbs items={getBreadcrumbItems()} />

      {/* Progress indicator */}
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <Typography variant="h6" sx={{ mr: 2 }}>
          Step {activeStep + 1} of 3
        </Typography>
        <LinearProgress 
          value={(activeStep + 1) * 33.33} 
          sx={{ flex: 1, height: 8, borderRadius: 4 }}
        />
      </Box>

      {/* Draft saved notification */}
      {showDraftSaved && (
        <Alert 
          severity="success" 
          sx={{ mb: 2 }}
          action={
            <Button color="inherit" size="small" onClick={() => setShowDraftSaved(false)}>
              Dismiss
            </Button>
          }
        >
          Draft saved automatically
        </Alert>
      )}

      <Typography variant="h4" component="h1" gutterBottom fontWeight="bold">
        Create Matter
      </Typography>

      <Card>
        <CardContent>
          <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          {activeStep === 0 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Select Type
              </Typography>

              {loading ? (
                <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
                  <Typography>Loading...</Typography>
                </Box>
              ) : (
                <Grid container spacing={2}>
                  {categories.map((category, index) => (
                    <Grid item xs={6} sm={4} md={3} key={category._id}>
                      <Card
                        sx={{
                          cursor: 'pointer',
                          border: selectedCategory?._id === category._id ? `2px solid #FFA500` : '1px solid #D1D3D4',
                          borderRadius: 3,
                          backgroundColor: selectedCategory?._id === category._id ? '#FFA500' : 'white',
                          color: selectedCategory?._id === category._id ? 'white' : 'inherit',
                          '&:hover': { borderColor: '#FFA500' },
                          height: 120,
                          display: 'flex',
                          flexDirection: 'column',
                          alignItems: 'center',
                          justifyContent: 'center',
                        }}
                        onClick={() => handleCategorySelect(category)}
                      >
                        <Box sx={{ color: selectedCategory?._id === category._id ? 'white' : Colors.primary, mb: 1 }}>
                          {getCategoryIcon(category.icon)}
                        </Box>
                        <Typography variant="body2" textAlign="center" fontWeight="medium">
                          {category.name}
                        </Typography>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              )}

              <Button
                variant="contained"
                fullWidth
                disabled={!selectedCategory}
                onClick={() => setActiveStep(1)}
                sx={{
                  backgroundColor: selectedCategory ? '#FFA500' : '#D3D3D3',
                  mt: 3,
                  borderRadius: 2,
                  height: 50,
                }}
              >
                Next
              </Button>
            </Box>
          )}

          {activeStep === 1 && selectedCategory && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Select Services
              </Typography>

              <FormControl component="fieldset" sx={{ width: '100%' }}>
                <RadioGroup value={selectedSubcategory?._id || ''}>
                  {subcategories.map((subcategory) => (
                    <FormControlLabel
                      key={subcategory._id}
                      value={subcategory._id}
                      control={<Radio />}
                      label={subcategory.name}
                      onClick={() => handleSubcategorySelect(subcategory)}
                      sx={{
                        border: '1px solid #D1D3D4',
                        borderRadius: 2,
                        margin: '8px 0',
                        padding: '12px 16px',
                        width: '100%',
                        backgroundColor: selectedSubcategory?._id === subcategory._id ? '#FFA500' : 'white',
                        color: selectedSubcategory?._id === subcategory._id ? 'white' : 'inherit',
                        '&:hover': {
                          backgroundColor: selectedSubcategory?._id === subcategory._id ? '#FFA500' : '#f5f5f5',
                        },
                        '& .MuiFormControlLabel-label': {
                          color: selectedSubcategory?._id === subcategory._id ? 'white' : 'inherit',
                          fontWeight: selectedSubcategory?._id === subcategory._id ? 600 : 400,
                        },
                      }}
                    />
                  ))}
                </RadioGroup>
              </FormControl>

              <Button
                variant="contained"
                fullWidth
                disabled={!selectedSubcategory}
                onClick={() => setActiveStep(2)}
                sx={{
                  backgroundColor: selectedSubcategory ? '#FFA500' : '#D3D3D3',
                  mt: 3,
                  borderRadius: 2,
                  height: 50,
                }}
              >
                Next
              </Button>
            </Box>
          )}

          {activeStep === 2 && (
            <Box>
              <Typography variant="h6" gutterBottom>
                Describe Your Matter
              </Typography>

              <TextField
                fullWidth
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                sx={{
                  mb: 3,
                  '& .MuiOutlinedInput-root': {
                    borderRadius: 2,
                    backgroundColor: 'white',
                  },
                }}
              />

              <TextField
                fullWidth
                placeholder="Describe your matter (Follow sample case description below)"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                multiline
                rows={8}
                helperText={`${description.length}/1000 characters`}
                error={description.length > 1000}
                sx={{
                  mb: 3,
                  '& .MuiOutlinedInput-root': {
                    borderRadius: 2,
                    backgroundColor: 'white',
                  },
                }}
              />

              <Card sx={{ backgroundColor: '#F4F6F8', mb: 3, borderRadius: 3, p: 2 }}>
                <Typography variant="subtitle2" fontWeight="bold" gutterBottom>
                  Sample Case Description
                </Typography>
                <Typography variant="body2" color="textSecondary">
                   Case Type<br/>
                   Who are you?<br/>
                  &nbsp;&nbsp;&nbsp;Petitioner/Applicant/Respondent<br/>
                   Court/Judge Name (In progress case)
                </Typography>
              </Card>

              <Button
                variant="contained"
                fullWidth
                disabled={!title.trim() || !description.trim() || description.length > 1000}
                onClick={handleSubmit}
                sx={{
                  backgroundColor: (title.trim() && description.trim() && description.length <= 1000) ? '#FFA500' : '#D3D3D3',
                  borderRadius: 2,
                  height: 50,
                }}
              >
                Submit
              </Button>
            </Box>
          )}

          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
            <Button
              variant="outlined"
              onClick={() => {
                if (activeStep > 0) {
                  setActiveStep(activeStep - 1);
                } else {
                  navigate('/dashboard');
                }
              }}
            >
              {activeStep === 0 ? 'Back to Dashboard' : 'Back'}
            </Button>

            {/* Keyboard shortcuts hint */}
            <Typography variant="caption" color="textSecondary" sx={{ alignSelf: 'center' }}>
               Tip: Press Ctrl+S to save draft, Ctrl+Enter to proceed
            </Typography>
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
};

export default PostMatter;
