import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  Grid,
  Alert,
  LinearProgress,
} from '@mui/material';
import {
  Gavel,
  Description,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';
import Breadcrumbs from '../components/Breadcrumbs';

interface RequestType {
  id: string;
  label: string;
  description: string;
  icon: React.ReactNode;
}

const AddRequest1: React.FC = () => {
  const navigate = useNavigate();
  const [selectedOption, setSelectedOption] = useState<string | null>(null);
  const [showDraftSaved, setShowDraftSaved] = useState(false);

  const options: RequestType[] = [
    {
      id: 'judicial',
      label: 'Judicial',
      description: 'e.g. Certified Copies, Court Case Follow-ups, Surety/Bail Bonds etc.',
      icon: <Gavel />,
    },
    {
      id: 'non_judicial',
      label: 'Non-Judicial',
      description: 'e.g. Birth Certificate, Death Certificate, Power of Attorney etc.',
      icon: <Description />,
    },
  ];

  // Auto-save draft functionality
  const saveDraft = () => {
    const draft = {
      selectedOption,
      timestamp: new Date().toISOString(),
    };
    localStorage.setItem('request-draft', JSON.stringify(draft));
    setShowDraftSaved(true);
    setTimeout(() => setShowDraftSaved(false), 2000);
  };

  // Load draft on component mount
  useEffect(() => {
    const savedDraft = localStorage.getItem('request-draft');
    if (savedDraft) {
      try {
        const draft = JSON.parse(savedDraft);
        setSelectedOption(draft.selectedOption || null);
      } catch (error) {
        console.error('Error loading draft:', error);
      }
    }
  }, []);

  // Auto-save when selection changes
  useEffect(() => {
    const timeoutId = setTimeout(saveDraft, 2000);
    return () => clearTimeout(timeoutId);
  }, [selectedOption]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.ctrlKey || e.metaKey) {
        switch (e.key) {
          case 's':
            e.preventDefault();
            saveDraft();
            break;
          case 'Enter':
            e.preventDefault();
            if (selectedOption) {
              handleNext();
            }
            break;
        }
      } else if (e.key === 'Escape') {
        navigate('/dashboard');
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [selectedOption]);

  const handleNext = () => {
    if (selectedOption) {
      // Clear draft when proceeding
      localStorage.removeItem('request-draft');
      navigate('/add-request-2', { state: { type: selectedOption } });
    }
  };

  return (
    <Box>
      {/* Breadcrumbs */}
      <Breadcrumbs items={[
        { label: 'Post Your Request', path: '/dashboard' },
        { label: 'Select Type', current: true }
      ]} />

      {/* Progress indicator */}
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <Typography variant="h6" sx={{ mr: 2 }}>
          Step 1 of 3
        </Typography>
        <LinearProgress 
          value={33.33} 
          sx={{ flex: 1, height: 8, borderRadius: 4 }}
        />
      </Box>

      {/* Draft saved notification */}
      {showDraftSaved && (
        <Alert 
          severity="success" 
          sx={{ mb: 2 }}
          action={
            <Button color="inherit" size="small" onClick={() => setShowDraftSaved(false)}>
              Dismiss
            </Button>
          }
        >
          Draft saved automatically
        </Alert>
      )}

      <Typography variant="h4" component="h1" gutterBottom fontWeight="bold">
        Add Request
      </Typography>

      <Card>
        <CardContent>
          <Box sx={{ mb: 4 }}>
            <Typography variant="h6" gutterBottom>
              Select Type
            </Typography>
          </Box>

          <Grid container spacing={3}>
            {options.map((option) => (
              <Grid item xs={12} sm={6} key={option.id}>
                <Card
                  sx={{
                    cursor: 'pointer',
                    border: selectedOption === option.id ? `2px solid #FFA500` : '1px solid #D1D3D4',
                    borderRadius: 3,
                    backgroundColor: selectedOption === option.id ? '#FFA500' : 'white',
                    color: selectedOption === option.id ? 'white' : 'inherit',
                    '&:hover': { borderColor: '#FFA500' },
                    height: 200,
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                  }}
                  onClick={() => setSelectedOption(option.id)}
                >
                  <CardContent sx={{ textAlign: 'center' }}>
                    <Box sx={{ 
                      color: selectedOption === option.id ? 'white' : Colors.primary, 
                      mb: 2,
                      display: 'flex',
                      justifyContent: 'center'
                    }}>
                      {option.icon}
                    </Box>
                    <Typography variant="h6" fontWeight="bold" gutterBottom>
                      {option.label}
                    </Typography>
                    <Typography variant="body2" color={selectedOption === option.id ? 'white' : 'textSecondary'}>
                      {option.description}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>

          <Button
            variant="contained"
            fullWidth
            disabled={!selectedOption}
            onClick={handleNext}
            sx={{
              backgroundColor: selectedOption ? '#FFA500' : '#D3D3D3',
              mt: 4,
              borderRadius: 2,
              height: 50,
            }}
          >
            Next
          </Button>

          {/* Keyboard shortcuts hint */}
          <Typography variant="caption" color="textSecondary" sx={{ display: 'block', textAlign: 'center', mt: 2 }}>
            ðŸ’¡ Tip: Press Ctrl+S to save draft, Ctrl+Enter to proceed
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
};

export default AddRequest1;
