sourcesContent:
import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  TextField,
  Stepper,
  Step,
  StepLabel,
  Grid,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  Alert,
  LinearProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Chip,
  useTheme,
  useMediaQuery,
  Snackbar,
  Backdrop,
  CircularProgress,
  Tooltip,
  Fade,
  Zoom,
} from '@mui/material';
import {
  Gavel,
  Description,
  Assignment,
  Business,
  Home,
  Work,
  AccountBalance,
  School,
  Save,
  Close,
  CheckCircle,
  Warning,
  Info,
  ArrowBack,
  ArrowForward,
  KeyboardArrowRight,
  KeyboardArrowLeft,
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';
import Breadcrumbs from '../components/Breadcrumbs';

interface MatterCategory {
  _id: string;
  name: string;
  icon: string;
}

interface MatterSubcategory {
  _id: string;
  name: string;
  icon: string;
  parent: string;
}

const PostMatter: React.FC = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const isSmallMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const [activeStep, setActiveStep] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState<MatterCategory | null>(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState<MatterSubcategory | null>(null);
  const [categories, setCategories] = useState<MatterCategory[]>([]);
  const [subcategories, setSubcategories] = useState<MatterSubcategory[]>([]);
  const [loading, setLoading] = useState(true);
  const [loadingSubcategories, setLoadingSubcategories] = useState(false);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [showDraftSaved, setShowDraftSaved] = useState(false);

  // Enhanced UX states
  const [showExitDialog, setShowExitDialog] = useState(false);
  const [showSubmitDialog, setShowSubmitDialog] = useState(false);
  const [showSuccessDialog, setShowSuccessDialog] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [snackbar, setSnackbar] = useState<{ open: boolean; message: string; severity: 'success' | 'error' | 'info' }>({
    open: false,
    message: '',
    severity: 'info'
  });

  // Mock data matching Android app structure exactly
  const mockCategories: MatterCategory[] = [
    { _id: '1', name: 'Family Law', icon: 'family' },
    { _id: '2', name: 'Civil Law', icon: 'civil' },
    { _id: '3', name: 'Criminal Law', icon: 'criminal' },
    { _id: '4', name: 'Corporate Law', icon: 'corporate' },
    { _id: '5', name: 'Employment Law', icon: 'employment' },
    { _id: '6', name: 'Property Law', icon: 'property' },
    { _id: '7', name: 'Tax Law', icon: 'tax' },
    { _id: '8', name: 'Banking Law', icon: 'banking' },
    { _id: '9', name: 'Intellectual Property', icon: 'ip' },
    { _id: '10', name: 'Immigration Law', icon: 'immigration' },
    { _id: '11', name: 'Environmental Law', icon: 'environment' },
    { _id: '12', name: 'Healthcare Law', icon: 'healthcare' },
  ];

  const mockSubcategories: MatterSubcategory[] = [
    // Family Law (1)
    { _id: '1-1', name: 'Divorce', icon: 'divorce', parent: '1' },
    { _id: '1-2', name: 'Child Custody', icon: 'custody', parent: '1' },
    { _id: '1-3', name: 'Child Support', icon: 'support', parent: '1' },
    { _id: '1-4', name: 'Adoption', icon: 'adoption', parent: '1' },
    { _id: '1-5', name: 'Marriage Registration', icon: 'marriage', parent: '1' },
    { _id: '1-6', name: 'Domestic Violence', icon: 'violence', parent: '1' },

    // Civil Law (2)
    { _id: '2-1', name: 'Property Disputes', icon: 'property', parent: '2' },
    { _id: '2-2', name: 'Contract Disputes', icon: 'contract', parent: '2' },
    { _id: '2-3', name: 'Tort Claims', icon: 'tort', parent: '2' },
    { _id: '2-4', name: 'Landlord-Tenant', icon: 'rental', parent: '2' },
    { _id: '2-5', name: 'Personal Injury', icon: 'injury', parent: '2' },
    { _id: '2-6', name: 'Medical Malpractice', icon: 'malpractice', parent: '2' },

    // Criminal Law (3)
    { _id: '3-1', name: 'Theft', icon: 'theft', parent: '3' },
    { _id: '3-2', name: 'Assault', icon: 'assault', parent: '3' },
    { _id: '3-3', name: 'Drug Offenses', icon: 'drugs', parent: '3' },
    { _id: '3-4', name: 'Fraud', icon: 'fraud', parent: '3' },
    { _id: '3-5', name: 'Cyber Crime', icon: 'cyber', parent: '3' },
    { _id: '3-6', name: 'White Collar Crime', icon: 'white-collar', parent: '3' },

    // Corporate Law (4)
    { _id: '4-1', name: 'Company Formation', icon: 'formation', parent: '4' },
    { _id: '4-2', name: 'Corporate Compliance', icon: 'compliance', parent: '4' },
    { _id: '4-3', name: 'Mergers & Acquisitions', icon: 'mergers', parent: '4' },
    { _id: '4-4', name: 'Intellectual Property', icon: 'ip', parent: '4' },
    { _id: '4-5', name: 'Corporate Governance', icon: 'governance', parent: '4' },
    { _id: '4-6', name: 'Securities Law', icon: 'securities', parent: '4' },

    // Employment Law (5)
    { _id: '5-1', name: 'Employment Contracts', icon: 'contract', parent: '5' },
    { _id: '5-2', name: 'Workplace Safety', icon: 'safety', parent: '5' },
    { _id: '5-3', name: 'Industrial Disputes', icon: 'disputes', parent: '5' },
    { _id: '5-4', name: 'Workers Compensation', icon: 'compensation', parent: '5' },
    { _id: '5-5', name: 'Trade Unions', icon: 'unions', parent: '5' },
    { _id: '5-6', name: 'Minimum Wage', icon: 'wage', parent: '5' },

    // Property Law (6)
    { _id: '6-1', name: 'Real Estate', icon: 'realestate', parent: '6' },
    { _id: '6-2', name: 'Property Registration', icon: 'registration', parent: '6' },
    { _id: '6-3', name: 'Land Disputes', icon: 'land', parent: '6' },
    { _id: '6-4', name: 'Property Tax', icon: 'tax', parent: '6' },
    { _id: '6-5', name: 'Rental Agreements', icon: 'rental', parent: '6' },
    { _id: '6-6', name: 'Property Development', icon: 'development', parent: '6' },

    // Tax Law (7)
    { _id: '7-1', name: 'Income Tax', icon: 'income', parent: '7' },
    { _id: '7-2', name: 'Corporate Tax', icon: 'corporate', parent: '7' },
    { _id: '7-3', name: 'Sales Tax', icon: 'sales', parent: '7' },
    { _id: '7-4', name: 'Property Tax', icon: 'property', parent: '7' },
    { _id: '7-5', name: 'Tax Disputes', icon: 'disputes', parent: '7' },
    { _id: '7-6', name: 'Tax Planning', icon: 'planning', parent: '7' },

    // Banking Law (8)
    { _id: '8-1', name: 'Banking Regulations', icon: 'regulations', parent: '8' },
    { _id: '8-2', name: 'Securities Law', icon: 'securities', parent: '8' },
    { _id: '8-3', name: 'Insurance Law', icon: 'insurance', parent: '8' },
