    // Intellectual Property (9)
    { _id: '9-1', name: 'Patent Law', icon: 'patent', parent: '9' },
    { _id: '9-2', name: 'Trademark Law', icon: 'trademark', parent: '9' },
    { _id: '9-3', name: 'Copyright Law', icon: 'copyright', parent: '9' },
    { _id: '9-4', name: 'Trade Secrets', icon: 'secrets', parent: '9' },
    { _id: '9-5', name: 'IP Litigation', icon: 'litigation', parent: '9' },
    { _id: '9-6', name: 'IP Licensing', icon: 'licensing', parent: '9' },

    // Immigration Law (10)
    { _id: '10-1', name: 'Visa Applications', icon: 'visa', parent: '10' },
    { _id: '10-2', name: 'Citizenship', icon: 'citizenship', parent: '10' },
    { _id: '10-3', name: 'Asylum Cases', icon: 'asylum', parent: '10' },
    { _id: '10-4', name: 'Deportation Defense', icon: 'deportation', parent: '10' },
    { _id: '10-5', name: 'Work Permits', icon: 'work-permit', parent: '10' },
    { _id: '10-6', name: 'Family Immigration', icon: 'family-immigration', parent: '10' },

    // Environmental Law (11)
    { _id: '11-1', name: 'Environmental Compliance', icon: 'compliance', parent: '11' },
    { _id: '11-2', name: 'Pollution Control', icon: 'pollution', parent: '11' },
    { _id: '11-3', name: 'Land Use Planning', icon: 'land-use', parent: '11' },
    { _id: '11-4', name: 'Natural Resources', icon: 'resources', parent: '11' },
    { _id: '11-5', name: 'Climate Change', icon: 'climate', parent: '11' },
    { _id: '11-6', name: 'Environmental Litigation', icon: 'litigation', parent: '11' },

    // Healthcare Law (12)
    { _id: '12-1', name: 'Medical Malpractice', icon: 'malpractice', parent: '12' },
    { _id: '12-2', name: 'Healthcare Compliance', icon: 'compliance', parent: '12' },
    { _id: '12-3', name: 'Patient Rights', icon: 'rights', parent: '12' },
    { _id: '12-4', name: 'Healthcare Contracts', icon: 'contract', parent: '12' },
    { _id: '12-5', name: 'Pharmaceutical Law', icon: 'pharma', parent: '12' },
    { _id: '12-6', name: 'Healthcare Insurance', icon: 'insurance', parent: '12' },
  ];

  const steps = ['Select Type', 'Select Services', 'Describe Matter'];

  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setCategories(mockCategories);
      setLoading(false);
    }, 1000);
  }, []);

  useEffect(() => {
    if (selectedCategory) {
      setLoadingSubcategories(true);
      // Simulate API call delay
      setTimeout(() => {
        const filtered = mockSubcategories.filter(sub => sub.parent === selectedCategory._id);
        setSubcategories(filtered);
        setLoadingSubcategories(false);
      }, 500);
    } else {
      setSubcategories([]);
    }
  }, [selectedCategory]);

  // Auto-save draft functionality
  const saveDraft = () => {
    const draft = {
      activeStep,
      selectedCategory,
      selectedSubcategory,
      title,
      description,
      timestamp: new Date().toISOString(),
    };
    localStorage.setItem('matter-draft', JSON.stringify(draft));
    setSnackbar({
      open: true,
      message: 'Draft saved automatically',
      severity: 'success'
    });
  };

  // Enhanced keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.ctrlKey || e.metaKey) {
        switch (e.key) {
          case 's':
            e.preventDefault();
            saveDraft();
            break;
          case 'Enter':
            e.preventDefault();
            if (activeStep < 2) {
              handleNext();
            } else {
              setShowSubmitDialog(true);
            }
            break;
        }
      }

      if (e.key === 'Escape') {
        if (activeStep > 0) {
          handleBack();
        } else {
          setShowExitDialog(true);
        }
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [activeStep, selectedCategory, selectedSubcategory, title, description]);

  // Auto-save on form changes
  useEffect(() => {
    const timeoutId = setTimeout(saveDraft, 2000);
    return () => clearTimeout(timeoutId);
  }, [activeStep, selectedCategory, selectedSubcategory, title, description]);

  const handleCategorySelect = (category: MatterCategory) => {
    setSelectedCategory(category);
    setSelectedSubcategory(null);
  };

  const handleSubcategorySelect = (subcategory: MatterSubcategory) => {
    setSelectedSubcategory(subcategory);
  };

  const handleNext = () => {
    if (activeStep < 2) {
      setActiveStep(activeStep + 1);
    }
  };

  const handleBack = () => {
    if (activeStep > 0) {
      setActiveStep(activeStep - 1);
    } else {
      navigate('/my-matters');
    }
  };

  const handleSubmit = async () => {
    setSubmitting(true);
    setShowSubmitDialog(false);

    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 2000));

    setSubmitting(false);
    setShowSuccessDialog(true);
  };

  const handleSuccessClose = () => {
    setShowSuccessDialog(false);
    navigate('/my-matters');
  };

  const getCategoryIcon = (iconName: string) => {
    const iconMap: { [key: string]: React.ReactElement } = {
      // Main categories
      family: <Home />,
      civil: <Assignment />,
      criminal: <Gavel />,
      corporate: <Business />,
      employment: <Work />,
      property: <Home />,
      tax: <AccountBalance />,
      banking: <AccountBalance />,

      // Subcategory icons
      divorce: <Home />,
      custody: <Home />,
      support: <Home />,
      adoption: <Home />,
      marriage: <Home />,
      violence: <Gavel />,
      contract: <Assignment />,
      tort: <Assignment />,
      rental: <Home />,
      injury: <Assignment />,
      malpractice: <Assignment />,
      theft: <Gavel />,
      assault: <Gavel />,
      drugs: <Gavel />,
      fraud: <Gavel />,
      cyber: <Gavel />,
      'white-collar': <Gavel />,
      formation: <Business />,
      compliance: <Business />,
      mergers: <Business />,
      ip: <Business />,
      governance: <Business />,
      securities: <Business />,
      safety: <Work />,
      disputes: <Work />,
      compensation: <Work />,
      unions: <Work />,
      wage: <Work />,
      realestate: <Home />,
      registration: <Home />,
      land: <Home />,
      development: <Home />,
      income: <AccountBalance />,
      sales: <AccountBalance />,
      planning: <AccountBalance />,
      regulations: <AccountBalance />,
      insurance: <AccountBalance />,
      debt: <AccountBalance />,
      investment: <AccountBalance />,

      // New category icons
      ip: <Business />,
      immigration: <Home />,
      environment: <Home />,
      healthcare: <Work />,

      // New subcategory icons
      patent: <Business />,
      trademark: <Business />,
      copyright: <Business />,
      secrets: <Business />,
      litigation: <Assignment />,
      licensing: <Business />,
      visa: <Home />,
      citizenship: <Home />,
      asylum: <Home />,
      deportation: <Gavel />,
      'work-permit': <Work />,
      'family-immigration': <Home />,
      'land-use': <Home />,
      resources: <Home />,
      climate: <Home />,
      rights: <Assignment />,
      pharma: <Work />,
    };
    return iconMap[iconName] || <Description />;
  };

  const getBreadcrumbItems = () => {
    const items: Array<{ label: string; path?: string; current?: boolean }> = [
      { label: 'Dashboard', path: '/dashboard' },
      { label: 'My Matters', path: '/my-matters' },
    ];

    if (activeStep >= 1) {
      items.push({ label: steps[0] });
    }

    if (activeStep >= 2) {
      items.push({ label: steps[1] });
    }

    if (activeStep === 2) {
      items.push({ label: steps[2], current: true });
    }

    return items;
  };

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: `linear-gradient(135deg, ${Colors.gray50} 0%, ${Colors.background} 100%)`,
      position: 'relative'
    }}>
      {/* Background Pattern */}
      <Box sx={{
        position: 'absolute',
        top: 0,
        right: 0,
        width: '300px',
        height: '300px',
        background: `linear-gradient(135deg, ${Colors.primary}15 0%, ${Colors.secondary}15 100%)`,
        borderRadius: '50%',
        transform: 'translate(50%, -50%)',
        zIndex: 0
      }} />

      <Box sx={{ position: 'relative', zIndex: 1, maxWidth: '100%', mx: 'auto', px: isMobile ? 2 : 4 }}>
        {/* Breadcrumbs */}
        <Breadcrumbs items={getBreadcrumbItems()} />

        {/* Enhanced Header */}
        <Box sx={{ 
          textAlign: 'center',
          mb: 4,
          pt: 2
        }}>
          <Typography 
            variant={isMobile ? "h4" : "h3"} 
            component="h1" 
            fontWeight="bold"
            sx={{
              background: `linear-gradient(135deg, ${Colors.primary} 0%, ${Colors.primaryDark} 100%)`,
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              mb: 2
            }}
          >
            Create Matter
          </Typography>
          <Typography variant="h6" color="textSecondary" sx={{ mb: 3 }}>
            Step {activeStep + 1} of 3 • {Math.round((activeStep + 1) * 33.33)}% Complete
          </Typography>
        </Box>

        {/* Enhanced Progress Bar */}
        <Card sx={{ 
          mb: 4,
          borderRadius: 3,
          boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
          background: 'rgba(255,255,255,0.95)',
          backdropFilter: 'blur(10px)'
        }}>
          <CardContent sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
              <Typography variant="body2" color="textSecondary" sx={{ minWidth: 80 }}>
                Progress
              </Typography>
              <LinearProgress 
                value={(activeStep + 1) * 33.33} 
                sx={{ 
                  flex: 1, 
                  height: 8, 
                  borderRadius: 4,
                  backgroundColor: Colors.gray200,
                  '& .MuiLinearProgress-bar': {
                    background: `linear-gradient(90deg, ${Colors.primary} 0%, ${Colors.primaryLight} 100%)`,
                    borderRadius: 4,
                  }
                }}
              />
              <Chip 
                label={`${Math.round((activeStep + 1) * 33.33)}%`}
                color="primary"
                size="small"
                sx={{ fontWeight: 'bold' }}
              />
            </Box>

            {/* Step Labels */}
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 2 }}>
              {steps.map((step, index) => (
                <Box key={step} sx={{ textAlign: 'center', flex: 1 }}>
                  <Typography 
                    variant="caption" 
                    sx={{ 
                      fontWeight: index <= activeStep ? 'bold' : 'normal',
                      color: index <= activeStep ? Colors.primary : Colors.gray500
