sourcesContent:
import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Chip,
  LinearProgress,
  Paper,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Stepper,
  Step,
  StepLabel,
  Tabs,
  Tab,
  Badge,
  IconButton,
} from '@mui/material';
import {
  TrendingUp,
  People,
  Assignment,
  Chat,
  CalendarToday,
  Notifications,
  Add,
  Description,
  Gavel,
  Business,
  School,
  Home,
  Work,
  AccountBalance,
  AccountBalanceWallet,
  WorkOutline,
  PostAdd,
  ListAlt,
} from '@mui/icons-material';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Colors } from '../assets/styles/colors';

interface ClientStats {
  totalRequests: number;
  activeRequests: number;
  totalLawyers: number;
  pendingResponses: number;
  completedMatters: number;
  recentMessages: number;
}

interface ClientRequest {
  id: string;
  title: string;
  type: string;
  service: string;
  status: string;
  lawyer?: string;
  createdAt: string;
  progress: number;
}

interface Lawyer {
  id: string;
  name: string;
  specialization: string;
  rating: number;
  cases: number;
  avatar?: string;
  isOnline: boolean;
}

interface ServiceCategory {
  id: string;
  name: string;
  icon: React.ReactNode;
  description: string;
  services: Service[];
}

interface Service {
  id: string;
  name: string;
  description: string;
  subServices?: Service[];
}

const ClientDashboard: React.FC = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState<ClientStats>({
    totalRequests: 0,
    activeRequests: 0,
    totalLawyers: 0,
    pendingResponses: 0,
    completedMatters: 0,
    recentMessages: 0,
  });

  const [requests, setRequests] = useState<ClientRequest[]>([]);
  const [lawyers, setLawyers] = useState<Lawyer[]>([]);
  const [openRequestDialog, setOpenRequestDialog] = useState(false);
  const [activeStep, setActiveStep] = useState(0);
  const [selectedType, setSelectedType] = useState<string>('');
  const [selectedCategory, setSelectedCategory] = useState<ServiceCategory | null>(null);
  const [selectedService, setSelectedService] = useState<Service | null>(null);
  const [selectedSubService, setSelectedSubService] = useState<Service | null>(null);
  const [tabValue, setTabValue] = useState(0);
  const [newRequest, setNewRequest] = useState({
    title: '',
    description: '',
  });

  // Service categories matching Android app
  const serviceCategories: ServiceCategory[] = [
    {
      id: 'judicial',
      name: 'Judicial',
      icon: <Gavel />,
      description: 'e.g. Certified Copies, Court Case Follow-ups, Surety/Bail Bonds etc.',
      services: [
        {
          id: 'certified_copies',
          name: 'Certified Copies',
          description: 'Get certified copies of court documents',
        },
        {
          id: 'court_followup',
          name: 'Court Case Follow-ups',
          description: 'Track and follow up on court cases',
        },
        {
          id: 'bail_bonds',
          name: 'Surety/Bail Bonds',
          description: 'Assistance with bail and surety bonds',
        },
        {
          id: 'legal_notices',
          name: 'Legal Notices',
          description: 'Draft and serve legal notices',
        },
      ]
    },
    {
      id: 'non_judicial',
      name: 'Non-Judicial',
      icon: <Description />,
      description: 'e.g. Birth Certificate, Death Certificate, Power of Attorney etc.',
      services: [
        {
          id: 'birth_certificate',
          name: 'Birth Certificate',
          description: 'Apply for birth certificate',
        },
        {
          id: 'death_certificate',
          name: 'Death Certificate',
          description: 'Apply for death certificate',
        },
        {
          id: 'power_attorney',
          name: 'Power of Attorney',
          description: 'Draft power of attorney documents',
        },
        {
          id: 'marriage_certificate',
          name: 'Marriage Certificate',
          description: 'Apply for marriage certificate',
        },
        {
          id: 'property_documents',
          name: 'Property Documents',
          description: 'Property registration and documentation',
        },
        {
          id: 'business_registration',
          name: 'Business Registration',
          description: 'Company and business registration',
        },
      ]
    }
  ];

  useEffect(() => {
    // TODO: Fetch client data from API
    setStats({
      totalRequests: 8,
      activeRequests: 3,
      totalLawyers: 5,
      pendingResponses: 2,
      completedMatters: 5,
      recentMessages: 4,
    });

    setRequests([
      {
        id: '1',
        title: 'Birth Certificate Application',
        type: 'Non-Judicial',
        service: 'Birth Certificate',
        status: 'In Progress',
        lawyer: 'Adv. Sarah Ahmed',
        createdAt: '2024-02-10',
        progress: 75,
      },
      {
        id: '2',
        title: 'Property Registration',
        type: 'Non-Judicial',
        service: 'Property Documents',
        status: 'Pending',
        lawyer: 'Adv. Muhammad Hassan',
        createdAt: '2024-02-08',
        progress: 45,
      },
      {
        id: '3',
        title: 'Legal Notice for Rent Dispute',
        type: 'Judicial',
        service: 'Legal Notices',
        status: 'Completed',
        lawyer: 'Adv. Ayesha Khan',
        createdAt: '2024-01-25',
        progress: 100,
      },
    ]);

    setLawyers([
      {
        id: '1',
        name: 'Adv. Sarah Ahmed',
        specialization: 'Family Law',
        rating: 4.8,
        cases: 45,
        isOnline: true,
      },
      {
        id: '2',
        name: 'Adv. Muhammad Hassan',
        specialization: 'Property Law',
        rating: 4.6,
        cases: 32,
        isOnline: false,
      },
      {
        id: '3',
        name: 'Adv. Ayesha Khan',
        specialization: 'Civil Law',
        rating: 4.9,
        cases: 67,
        isOnline: true,
      },
    ]);
  }, []);

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'completed':
        return Colors.success;
      case 'in progress':
        return Colors.warning;
      case 'pending':
        return Colors.error;
      default:
        return Colors.gray500;
    }
  };

  const handleAddRequest = () => {
    setOpenRequestDialog(true);
    setActiveStep(0);
    setSelectedType('');
    setSelectedCategory(null);
    setSelectedService(null);
    setSelectedSubService(null);
  };

  const handleCloseRequestDialog = () => {
    setOpenRequestDialog(false);
    setActiveStep(0);
    setSelectedType('');
    setSelectedCategory(null);
    setSelectedService(null);
    setSelectedSubService(null);
    setNewRequest({ title: '', description: '' });
  };

  const handleTypeSelect = (type: string) => {
    setSelectedType(type);
    setActiveStep(1);
  };

  const handleCategorySelect = (category: ServiceCategory) => {
    setSelectedCategory(category);
    setActiveStep(2);
  };

  const handleServiceSelect = (service: Service) => {
    setSelectedService(service);
    if (service.subServices && service.subServices.length > 0) {
      setActiveStep(3);
    } else {
      setActiveStep(4);
    }
  };

  const handleSubServiceSelect = (subService: Service) => {
    setSelectedSubService(subService);
    setActiveStep(4);
  };

  const handleSaveRequest = () => {
    // TODO: Save request to API
    const requestToAdd: ClientRequest = {
      id: Date.now().toString(),
      title: newRequest.title,
      type: selectedType,
      service: selectedService?.name || '',
      status: 'Pending',
      createdAt: new Date().toISOString().split('T')[0],
      progress: 0,
    };

    setRequests([requestToAdd, ...requests]);
    setStats(prev => ({
      ...prev,
      totalRequests: prev.totalRequests + 1,
      activeRequests: prev.activeRequests + 1,
    }));

    handleCloseRequestDialog();
  };

  const handleQuickAction = (action: string) => {
    switch (action) {
      case 'addRequest':
        navigate('/add-request-1'); // Navigate to AddRequest1 like Android
        break;
      case 'postMatter':
        navigate('/post-matter'); // Navigate to CreateMatter1 like Android
        break;
      case 'myRequests':
        navigate('/my-requests');
        break;
      case 'myMatters':
        navigate('/my-matters');
        break;
      case 'chat':
        navigate('/chat');
        break;
      case 'notifications':
        navigate('/notifications');
        break;
      case 'settings':
        navigate('/settings');
        break;
      default:
        break;
    }
  };

  const StatCard: React.FC<{
    title: string;
    value: number;
    icon: React.ReactNode;
    color: string;
  }> = ({ title, value, icon, color }) => (
    <Card sx={{ height: '100%' }}>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Box>
            <Typography color="textSecondary" gutterBottom variant="body2">
              {title}
            </Typography>
            <Typography variant="h4" component="div" fontWeight="bold" color={color}>
              {value}
            </Typography>
          </Box>
          <Avatar sx={{ bgcolor: color, width: 56, height: 56 }}>
            {icon}
          </Avatar>
        </Box>
      </CardContent>
    </Card>
  );

  const steps = ['Select Type', 'Select Services', 'Select Service', 'Select Sub-Service', 'Describe Your Need'];

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom fontWeight="bold">
        Welcome back, {user?.first_name}!
      </Typography>

      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Total Requests"
            value={stats.totalRequests}
            icon={<Assignment />}
            color={Colors.primary}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Active Requests"
            value={stats.activeRequests}
            icon={<TrendingUp />}
            color={Colors.success}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="My Lawyers"
            value={stats.totalLawyers}
            icon={<People />}
            color={Colors.secondary}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Pending Responses"
            value={stats.pendingResponses}
            icon={<Notifications />}
            color={Colors.warning}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Completed Matters"
            value={stats.completedMatters}
            icon={<Assignment />}
            color={Colors.success}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={4}>
          <StatCard
            title="Recent Messages"
            value={stats.recentMessages}
            icon={<Chat />}
            color={Colors.info}
          />
        </Grid>
      </Grid>

      {/* Post Your Matter Section - Matching Android App */}
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" fontWeight="bold" gutterBottom>
              Legal Matters
            </Typography>
            <Typography variant="body1" color="textSecondary">
              Post your legal cases and matters for lawyer assistance
            </Typography>
          </Box>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card
                sx={{
                  cursor: 'pointer',
                  backgroundColor: '#5491C9',
                  color: 'white',
                  '&:hover': { backgroundColor: '#4A7FB8' },
                }}
                onClick={() => navigate('/post-matter')}
              >
                <CardContent sx={{ py: 3 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Box sx={{ width: '40%', textAlign: 'center' }}>
                      <Gavel sx={{ fontSize: 48, color: 'white' }} />
                    </Box>
                    <Box sx={{ width: '60%' }}>
                      <Typography variant="h6" fontWeight="bold">
                        Post Your Matter
                      </Typography>
                    </Box>
                  </Box>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card
                sx={{
                  cursor: 'pointer',
                  backgroundColor: 'white',
                  border: '2px solid #5491C9',
                  '&:hover': { backgroundColor: '#F0F8FF' },
                }}
                onClick={() => navigate('/my-matters')}
              >
                <CardContent sx={{ py: 3 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Box sx={{ width: '40%', textAlign: 'center' }}>
                      <Assignment sx={{ fontSize: 48, color: '#404B53' }} />
                    </Box>
                    <Box sx={{ width: '60%' }}>
                      <Typography variant="h6" fontWeight="bold" color="#404B53">
                        My Matters
                      </Typography>
                    </Box>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      {/* Paralegal Assistance Section - Matching XD Design */}
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Box sx={{ mb: 3 }}>
            <Typography variant="h5" fontWeight="bold" gutterBottom>
              Paralegal Assistance
            </Typography>
            <Typography variant="body1" color="textSecondary">
              Get your legal/official documents e.g. Birth Certificate
            </Typography>
          </Box>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card
                sx={{
                  cursor: 'pointer',
                  backgroundColor: '#EAA141',
                  color: 'white',
                  '&:hover': { backgroundColor: '#D8943A' },
                }}
                onClick={() => handleQuickAction('addRequest')}
              >
                <CardContent sx={{ py: 3 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Box sx={{ width: '40%', textAlign: 'center' }}>
                      <PostAdd sx={{ fontSize: 48, color: 'white' }} />
                    </Box>
                    <Box sx={{ width: '60%' }}>
                      <Typography variant="h6" fontWeight="bold">
                        Post Your Request
                      </Typography>
                    </Box>
                  </Box>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card
                sx={{
                  cursor: 'pointer',
                  backgroundColor: 'white',
                  border: '2px solid #EAA141',
                  '&:hover': { backgroundColor: '#FFF8F0' },
                }}
                onClick={() => navigate('/my-requests')}
              >
                <CardContent sx={{ py: 3 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Box sx={{ width: '40%', textAlign: 'center' }}>
                      <ListAlt sx={{ fontSize: 48, color: '#404B53' }} />
                    </Box>
                    <Box sx={{ width: '60%' }}>
                      <Typography variant="h6" fontWeight="bold" color="#404B53">
                        My Requests
                      </Typography>
                    </Box>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      <Grid container spacing={3}>
        <Grid item xs={12} lg={8}>
          <Card>
            <CardContent>
              <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
                <Tabs value={tabValue} onChange={(e, newValue) => setTabValue(newValue)}>
                  <Tab label="My Requests" />
                  <Tab label="My Lawyers" />
                </Tabs>
              </Box>

              {tabValue === 0 && (
                <Box>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                    <Typography variant="h6" fontWeight="bold">
                      Recent Requests
                    </Typography>
                    <Button variant="outlined" color="primary" onClick={() => navigate('/my-requests')}>
                      View All
                    </Button>
                  </Box>

                  <List>
                    {requests.map((request, index) => (
                      <React.Fragment key={request.id}>
                        <ListItem alignItems="flex-start">
                          <ListItemAvatar>
                            <Avatar sx={{ bgcolor: Colors.primary }}>
                              {index + 1}
                            </Avatar>
                          </ListItemAvatar>
                          <ListItemText
                            primary={
                              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                <Typography variant="subtitle1" fontWeight="bold">
                                  {request.title}
                                </Typography>
                                <Chip
                                  label={request.status}
                                  size="small"
                                  sx={{
                                    backgroundColor: getStatusColor(request.status),
                                    color: 'white',
                                  }}
                                />
                              </Box>
                            }
                            secondary={
                              <Box>
                                <Typography variant="body2" color="textSecondary">
                                  Type: {request.type}  Service: {request.service}
                                </Typography>
                                {request.lawyer && (
                                  <Typography variant="body2" color="textSecondary">
                                    Lawyer: {request.lawyer}
                                  </Typography>
                                )}
                                <Typography variant="body2" color="textSecondary">
                                  Created: {request.createdAt}
                                </Typography>
                                <Box sx={{ mt: 1 }}>
                                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 0.5 }}>
                                    <Typography variant="body2" color="textSecondary">
                                      Progress
                                    </Typography>
                                    <Typography variant="body2" color="textSecondary">
                                      {request.progress}%
                                    </Typography>
                                  </Box>
                                  <LinearProgress
                                    variant="determinate"
                                    value={request.progress}
                                    sx={{
                                      height: 6,
                                      borderRadius: 3,
                                      backgroundColor: Colors.gray200,
                                      '& .MuiLinearProgress-bar': {
                                        backgroundColor: Colors.primary,
                                      },
                                    }}
                                  />
                                </Box>
                              </Box>
                            }
                          />
                        </ListItem>
                        {index < requests.length - 1 && <Divider variant="inset" component="li" />}
                      </React.Fragment>
                    ))}
                  </List>
                </Box>
              )}

              {tabValue === 1 && (
                <Box>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                    <Typography variant="h6" fontWeight="bold">
                      My Lawyers
                    </Typography>
                    <Button variant="outlined" color="primary" onClick={() => navigate('/my-lawyers')}>
                      Find More Lawyers
                    </Button>
                  </Box>

                  <Grid container spacing={2}>
                    {lawyers.map((lawyer) => (
                      <Grid item xs={12} sm={6} key={lawyer.id}>
                        <Card sx={{ p: 2 }}>
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                            <Badge
                              overlap="circular"
                              anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                              badgeContent={
                                <Box
                                  sx={{
                                    width: 12,
                                    height: 12,
                                    borderRadius: '50%',
                                    backgroundColor: lawyer.isOnline ? Colors.success : Colors.gray400,
                                    border: '2px solid white',
                                  }}
                                />
                              }
                            >
                              <Avatar sx={{ bgcolor: Colors.primary, width: 50, height: 50 }}>
                                {lawyer.name.charAt(0)}
                              </Avatar>
                            </Badge>
                            <Box sx={{ flex: 1 }}>
                              <Typography variant="subtitle1" fontWeight="bold">
                                {lawyer.name}
                              </Typography>
                              <Typography variant="body2" color="textSecondary">
                                {lawyer.specialization}
                              </Typography>
                              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mt: 0.5 }}>
                                <Typography variant="body2" color="textSecondary">
                                   {lawyer.rating}
                                </Typography>
                                <Typography variant="body2" color="textSecondary">
                                   {lawyer.cases} cases
                                </Typography>
                              </Box>
                            </Box>
                            <IconButton 
                              size="small" 
                              sx={{ color: Colors.primary }}
                              onClick={() => navigate('/chat')}
                            >
                              <Chat />
                            </IconButton>
                          </Box>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>
                </Box>
              )}
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} lg={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" fontWeight="bold" gutterBottom>
                Quick Actions
              </Typography>

              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                <Button
                  variant="contained"
                  fullWidth
                  startIcon={<Add />}
                  sx={{ backgroundColor: Colors.primary }}
                  onClick={() => handleQuickAction('addRequest')}
                >
                  Post Your Request
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  startIcon={<People />}
                  onClick={() => handleQuickAction('findLawyer')}
                >
                  Find Lawyers
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  startIcon={<Chat />}
                  onClick={() => handleQuickAction('sendMessage')}
                >
                  Send Message
                </Button>
                <Button
                  variant="outlined"
                  fullWidth
                  color="primary"
                  startIcon={<Description />}
                  onClick={() => handleQuickAction('viewDocuments')}
                >
                  View Documents
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Post Request Dialog - Matching Android App Flow */}
      <Dialog open={openRequestDialog} onClose={handleCloseRequestDialog} maxWidth="md" fullWidth>
        <DialogTitle>
          <Box sx={{ mb: 2 }}>
            <Typography variant="h6" fontWeight="bold">
              Post Your Request
            </Typography>
            <Stepper activeStep={activeStep} sx={{ mt: 2 }}>
              {steps.map((label) => (
                <Step key={label}>
                  <StepLabel>{label}</StepLabel>
                </Step>
              ))}
            </Stepper>
          </Box>
        </DialogTitle>
        <DialogContent>
          {activeStep === 0 && (
            <Box sx={{ pt: 2 }}>
              <Typography variant="h6" gutterBottom>
                Select Type
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <Card
                    sx={{
                      cursor: 'pointer',
                      border: selectedType === 'judicial' ? `2px solid ${Colors.primary}` : '1px solid #e0e0e0',
                      '&:hover': { borderColor: Colors.primary },
                    }}
                    onClick={() => handleTypeSelect('judicial')}
                  >
                    <CardContent sx={{ textAlign: 'center', py: 3 }}>
                      <Gavel sx={{ fontSize: 48, color: Colors.primary, mb: 2 }} />
                      <Typography variant="h6" fontWeight="bold">
                        Judicial
                      </Typography>
                      <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                        e.g. Certified Copies, Court Case Follow-ups, Surety/Bail Bonds etc.
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Card
                    sx={{
                      cursor: 'pointer',
                      border: selectedType === 'non_judicial' ? `2px solid ${Colors.primary}` : '1px solid #e0e0e0',
                      '&:hover': { borderColor: Colors.primary },
                    }}
                    onClick={() => handleTypeSelect('non_judicial')}
                  >
                    <CardContent sx={{ textAlign: 'center', py: 3 }}>
                      <Description sx={{ fontSize: 48, color: Colors.primary, mb: 2 }} />
                      <Typography variant="h6" fontWeight="bold">
                        Non-Judicial
                      </Typography>
                      <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                        e.g. Birth Certificate, Death Certificate, Power of Attorney etc.
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              </Grid>
            </Box>
          )}

          {activeStep === 1 && (
            <Box sx={{ pt: 2 }}>
              <Typography variant="h6" gutterBottom>
                Select Services
              </Typography>
              <Grid container spacing={2}>
                {serviceCategories
                  .filter(cat => cat.id === selectedType)
                  .map((category) => (
                    <Grid item xs={12} sm={6} key={category.id}>
                      <Card
                        sx={{
                          cursor: 'pointer',
                          border: selectedCategory?.id === category.id ? `2px solid ${Colors.primary}` : '1px solid #e0e0e0',
                          '&:hover': { borderColor: Colors.primary },
                        }}
                        onClick={() => handleCategorySelect(category)}
                      >
                        <CardContent sx={{ textAlign: 'center', py: 3 }}>
                          <Box sx={{ color: Colors.primary, mb: 1 }}>
                            {category.icon}
                          </Box>
                          <Typography variant="h6" fontWeight="bold">
                            {category.name}
                          </Typography>
                          <Typography variant="body2" color="textSecondary">
                            {category.services.length} services available
                          </Typography>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
              </Grid>
            </Box>
          )}

          {activeStep === 2 && selectedCategory && (
            <Box sx={{ pt: 2 }}>
              <Typography variant="h6" gutterBottom>
                Select Service - {selectedCategory.name}
              </Typography>
              <Grid container spacing={2}>
                {selectedCategory.services.map((service) => (
                  <Grid item xs={12} sm={6} key={service.id}>
                    <Card
                      sx={{
                        cursor: 'pointer',
                        border: selectedService?.id === service.id ? `2px solid ${Colors.primary}` : '1px solid #e0e0e0',
                        '&:hover': { borderColor: Colors.primary },
                      }}
                      onClick={() => handleServiceSelect(service)}
                    >
                      <CardContent sx={{ textAlign: 'center', py: 2 }}>
                        <Typography variant="subtitle1" fontWeight="medium">
                          {service.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                          {service.description}
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {activeStep === 3 && selectedService && selectedService.subServices && (
            <Box sx={{ pt: 2 }}>
              <Typography variant="h6" gutterBottom>
                Select Sub-Service - {selectedService.name}
              </Typography>
              <Grid container spacing={2}>
                {selectedService.subServices.map((subService) => (
                  <Grid item xs={12} sm={6} key={subService.id}>
                    <Card
                      sx={{
                        cursor: 'pointer',
                        border: selectedSubService?.id === subService.id ? `2px solid ${Colors.primary}` : '1px solid #e0e0e0',
                        '&:hover': { borderColor: Colors.primary },
                      }}
                      onClick={() => handleSubServiceSelect(subService)}
                    >
                      <CardContent sx={{ textAlign: 'center', py: 2 }}>
                        <Typography variant="subtitle1" fontWeight="medium">
                          {subService.name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                          {subService.description}
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {activeStep === 4 && (
            <Box sx={{ pt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
              <Typography variant="h6" gutterBottom>
                Describe Your Need
              </Typography>
              <Typography variant="body2" color="textSecondary" gutterBottom>
                Selected Document: {selectedService?.name}
                {selectedSubService && ` - ${selectedSubService.name}`}
              </Typography>
              <TextField
                fullWidth
                label="Title"
                value={newRequest.title}
                onChange={(e) => setNewRequest({ ...newRequest, title: e.target.value })}
                required
              />
              <TextField
                fullWidth
                label="Describe your requirements"
                value={newRequest.description}
                onChange={(e) => setNewRequest({ ...newRequest, description: e.target.value })}
                multiline
                rows={6}
                required
              />
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseRequestDialog}>Cancel</Button>
          {activeStep > 0 && (
            <Button onClick={() => setActiveStep(activeStep - 1)}>
              Back
            </Button>
          )}
          {activeStep < 4 && selectedType && (
            <Button 
              variant="contained" 
              onClick={() => setActiveStep(activeStep + 1)}
              sx={{ backgroundColor: Colors.primary }}
            >
              Next
            </Button>
          )}
          {activeStep === 4 && (
            <Button 
              variant="contained" 
              onClick={handleSaveRequest}
              disabled={!newRequest.title || !newRequest.description}
              sx={{ backgroundColor: Colors.primary }}
            >
              Submit
            </Button>
          )}
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default ClientDashboard;
